style.css:
@-moz-document domain("new.vk.com") {
#page_layout {
	width: 1275px !important;	
}
#page_body {
    width: 1100px !important;
}
.page_photo {
	    background: rgba(0,0,0,0);
    	box-shadow: none;
}
.page_avatar_img {
	border-radius: 2px;
    border: 0.5vh solid #fff;
    box-shadow: 0 1px 0 0 #e1e2e6, 0 0 0 1px #e6e7eb;
}
.chat_onl_height {
	border-radius: 50% 50% 0 0 !important;
}
.narrow_column {
    width: 250px;
}
.page_action_left {
    width: 218px !important;
}
a.button_link.cut_left {
	width: 180px;
}

.wide_column_wrap {
    margin-left: 270px !important;
}
.narrow_column_wrap {
	float: left !important;
}

.chat_onl_inner {
    border-radius: 25px 25px 0 0;
}
._audio_rows_header.audio_rows_header{
    width: auto !important;
}
div#page_header {
	width: 1275px !important;	
}
a#profile_edit_act {
	background: #6287AE;
    color: #fff;
    box-shadow: 0 1px 0 0 #e1e2e6, 0 0 0 1px #e6e7eb;
}
.owner_photo_bubble {
	width: 250px !important;
}
.im-chat-input--textarea.fl_l._im_text_input._emoji_field_wrap {
	width: 595px;
}
a#profile_edit_act:hover {
	background: #678EB4;	
}
::-webkit-scrollbar {
    width: 10px;
}
 
::-webkit-scrollbar-thumb {
    background: #597DA3;
    border-radius: 2px;
}
::-webkit-scrollbar-track-piece {
	background: #ccc;
}
body {
overflow-x: hidden;
}
#ads_left {
	display: none !important;
}
}

@-moz-document regexp("((?=.*?\\/friends).*|(?=.*?\\/groups).*|(?=.*?\\/feed).*|(?=.*?\\/fave).*|(?=.*?\\/docs).*|(?=.*?\\/audios).*)") {
.wide_column_wrap {
    margin-left: 0 !important;
}
.narrow_column_wrap {
    float: right !important;
}
}

@-moz-document regexp("(?=.*?\\/im).*|") {
.im-page--dialogs.page_block {
    float: right;
}
ul#im_dialogs {
    float: right;
    width: 90px;
    -webkit-transition: width 0.1s ease-out 0.01s;
     -moz-transition: width 0.1s ease-out 0.01s;
     -o-transition: width 0.1s ease-out 0.01s;
     transition: width 0.1s ease-out 0.01s;
}
ul#im_dialogs:hover {
	width: 316px;
}
.im-page--history.page_block._im_page_history {
margin-left: 0px;}
.im-page-history-w {
	width: auto !important;
}
.im-page--dialogs.page_block {
 box-shadow: none !important;
}
.im-page--dialogs-footer.ui_grey_block._im_dialogs_settings{
    box-shadow: 0 1px 0 0 #e1e2e6, 0 0 0 1px #e6e7eb;
}
input#im_dialogs_search {
	box-shadow: 0 1px 0 0 #e1e2e6, 0 0 0 1px #e6e7eb;
}
.nim-dialog .nim-dialog--content {
    display: block;
    left: 25px
}
.nim-dialog:hover .nim-dialog--content {
    left: 5px;
}
.nim-dialog .nim-dialog--date._im_dialog_date {
    display: none;
    right: 20px;
}
.nim-dialog:hover .nim-dialog--date._im_dialog_date {
    display: block;
}
/**/
}
