style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.basilmarket.com/") {

/* Opera */
div[style="padding:0;text-align:center;margin-top:0px;margin-bottom:10px;"], div[style="width:300px;float:right;"], div[style="text-align:center;margin-top:5px;margin-bottom:5px;"], div[style="text-align:center;margin-top:10px;"], div[style="text-align:center;margin-top:10px;padding:0;"] script
	{
		display: none !important;
	}
/* Firefox */
div[style="padding: 0pt; text-align: center; margin-top: 0px; margin-bottom: 10px;"], div[style="width: 300px; float: right;"], div[style="text-align: center; margin-top: 10px;"], div[style="text-align: center; margin-top: 5px; margin-bottom: 5px;"], div[style="padding: 0pt; text-align: center; margin-top: 10px;"] script
	{
		display: none !important;
	}

/* Remove empty space */
#banner_container_728x90, #banner_container_300x250
	{
		display: none !important;
	}

}
