style.css:
@-moz-document url-prefix("http://www.dumbingofage.com/") {
body.custom-background {
    background-color: black;
    color: white;
}
div#sidebar-aboveheader,
div#header {
    /*Hides Banner */
    
    display: none;
}
#content-wrapper {
    background-color: #292F33;
}
a:visited {
    text-decoration: none;
    color: yellow;
}
a:link {
    text-decoration: none;
    color: yellow;
}
a:visited {
    color: gold;
}
a:link {
    color: yellow;
}
.post {
    padding: 5px;
    border: 1px solid darkslateblue;
    border-bottom: 1px solid darkslateblue;
    border-width: 3px 3px 3px;
    background: #1d4a5d;
    color: white;
}
.plugin, .widget {
    color: white;
    background-color: #292F33;
}
.textwidget
{
    color: maroon;
}

}
