style.css:
@-moz-document url-prefix("http://www.matchup.gg/") {
#header,
.homepage #footer,
#build-content,
#trinkets-content,
#starting-items-content,
.situational-items-content,
#skill-content,
.header-analysis-winrate-percentage {
    background: #262626;
}
body,
.homepage,
.team-matchup-damage-legend-item,
.content-section-header::before,
.build-legend-item,
.skill-item .skill-level-active,
#header-opponent-change {
    background: #1C1C1C;
    color: #f6f6f6;
}
#header-opponent-change:hover {
    color: #666;
}
h6,
strong,
#footer,
#header-analysis-context,
#header-analysis-role {
    color: #f6f6f6;
}
.search-region-selector-popover-item,
.header-analysis-winrate-caption {
    color: #f6f6f6;
}
}
