style.css:
@-moz-document domain("ibood.com") {
.cf, body {
    background: #000;
}

.menu-top {
    background: #000 !important;
}

.menu-bottom {
    background: #000;
}

header .header-inner {
    background: #1F1F1F;
    padding-bottom: 15px;
}

.primary-nav>ul>li:hover>a, .primary-nav>ul>li.active>a {
    background: #1F1F1F;
}

img {opacity: 0.4; }

img:hover {opacity: 1.0;}

section .section-inner {
    background: #000;
}

.footer-shops {
    background: #000;
}

.footer-ibood {
    background: #000 !important;
    color: #fff !important;
}

.primary-offer .price, .primary-offer h2 span, .active-topics th {
     background: transparent !important; 
}

.active-topics {border:0;}

.widget-wrap {
    background: #1F1F1F;
    color: #666;
}

.widget-box, .offer-detail {
    position: relative;
    -moz-box-shadow: 1px 1px 1px 1px #999;
    -webkit-box-shadow: 1px 1px 1px 1px #1F1F1F;
    box-shadow: 1px 1px 1px 1px #1F1F1F;
}
}
