/*youtube-gtk3-dark-theme
*@Author: Thaer Maddah
*@email: thaeronline@gmail.com
*@Description: fix Firefox color bug on YouTube when apply dark theme on gtk3 under GNOME 3.18 or higher
*/

@-moz-document domain(youtube.com)
 {
     #masthead-search-term.search-term.yt-uix-form-input-bidi {
        background-color: #FFF;
        color: black;
     }
     
     .yt-uix-form-input-text {
        background-color: #FFF;
        color: black;
     }
  
     .yt-uix-form-input-textarea {
        background-color: #FFF;
        color: black;
     }
    
    .yt-uix-tooltip{
       color: black;
    }
}
