style.css:
@-moz-document domain("eldarya.fr") {
/*BASIC*/
#container .page-main-container {
    background-color: rgba(255, 255, 255, 0.5);
    border-radius: 10px;
    padding: 20px;
    min-height: 400px;
}
/*TOP BAR*/
body {
    background: #4da8fd url("http://nsa37.casimages.com/img/2016/05/08/160508035859407328.png") fixed top center;
    background-size: cover;
    overflow-y: scroll;
}
#header #header-top {
    position: absolute;
    top: 0;
    background-color: #10134A;
    height: 39px;
    width: 100%;
}
#player-info-level {
    font-family: Magnel, serif;
    color: #A1D0FF;
    font-size: 23px;
    font-weight: 400;
    text-align: center;
    line-height: 29px;
    border-radius: 50%;
    background-color: #10134A;
    box-shadow: 0 0 5px 0 rgba(25, 25, 25, 0.25);
    width: 33px;
    height: 33px;
    position: absolute;
    top: -8px;
    right: -8px;
    cursor: default;
    z-index: 2;
}
#player-headerPortrait {
    width: 92px;
    height: 92px;
    border-radius: 50%;
    border: solid 4px #10134A;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.3);
    left: -25px;
    top: 15px;
    background-color: #545A80;
}
#player-info-dots .player-info-dot.lit {
    background-color: #083779;
}
#pet-headerPortrait {
    top: 35px;
    height: 73px;
    width: 73px;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    border-radius: 50%;
    border: solid 4px #10134A;
    box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.3);
    background: #A1D0FF;
}
#player-infos .player-name {
    color: #A1D0FF;
    font-weight: 600;
    font-family: "Banda Regular", sans-serif;
    position: relative;
    height: 26px;
    line-height: 26px;
    display: inline-block;
    border: solid 2px #A1D0FF;
    background-color: #10134A;
    -webkit-transform: skew(-12deg);
    -moz-transform: skew(-12deg);
    -o-transform: skew(-12deg);
    -webkit-backface-visibility: hidden;
}
#player-infos .player-currencies {
    background-color: #10134A;
    border-radius: 20px;
    height: 25px;
    line-height: 25px;
    font-family: Banda Semi Bold, sans-serif;
    font-weight: 600;
    padding: 0 10px;
    margin-top: 15px;
}
#main-menu .main-menu-episode {
    background-image: url("http://nsa38.casimages.com/img/2016/05/08/160508042652228784.png");
}
#main-menu .main-menu-minigames {
    background-image: url("http://nsa38.casimages.com/img/2016/05/08/160508044501543321.png");
}
#main-menu .main-menu-mall {
    background-image: url("http://nsa37.casimages.com/img/2016/05/08/160508044501810284.png");
}
#main-menu .main-menu-marketplace {
    background-image: url("http://nsa38.casimages.com/img/2016/05/08/16050804450289151.png");
}
#main-menu .main-menu-ranking {
    background-image: url("http://nsa38.casimages.com/img/2016/05/08/160508044501465766.png");
}
#main-menu .main-menu-library {
    background-image: url("http://nsa38.casimages.com/img/2016/05/08/160508044501891174.png");
}
#main-menu .main-menu-messaging {
    background-image: url("http://nsa37.casimages.com/img/2016/05/08/160508044502362851.png");
}
#main-menu .main-menu-bank {
    background-image: url("http://nsa37.casimages.com/img/2016/05/08/160508044501386665.png");
}
#footer {
    font-family: Banda Bold, sans-serif;
    position: absolute;
    height: 42px;
    width: 100%;
    bottom: 0;
    left: 0;
    background-color: #10134A;
}
#main-menu .menu-label span {
    background-color: #545A80;
    padding: 5px 8px;
    border-radius: 20px;
    margin-right: -100%;
    display: inline-block;
}
#main-menu .main-menu-messaging .menu-label span {
    background-color: #545A80;
}
#main-menu .main-menu-bank .menu-label span {
    background-color: #195431;
}
/*HOMEPAGE*/
#index-news {
    display: inline-block;
    vertical-align: top;
    height: 346px;
    width: 249px;
    margin: 20px 0 0;
    background-color: #083779;
    border-radius: 10px;
    border: 2px solid #083779;
    overflow: hidden;
}
#index-ranking {
    float: left;
    display: inline-block;
    height: 346px;
    width: 249px;
    background-color: #083779;
    border-radius: 10px;
    border: 2px solid #083779;
    overflow: hidden;
    margin: 20px 20px 0 0;
}
#news-list .news {
    background: #fff no-repeat 20px;
    position: relative;
    border-top: 2px solid #083779;
    height: 50px;
}
#ranking-list > div {
    background: #fff no-repeat 20px;
    background-size: 75px;
    position: relative;
    border-top: 2px solid #083779;
}
.index-carousel {
    position: relative;
    height: 231px;
    width: 522px;
    border-radius: 10px;
    border: 2px solid #1E276F;
    background-color: #CCDBEF;
    overflow: hidden;
}
#ranking-list > div {
    background: #CCDBEF no-repeat 20px;
    background-size: 75px;
    position: relative;
    border-top: 2px solid #083779;
}
#ranking-list .guard-4 {
    background: #CCDBEF url('http://www.eldarya.fr/static/img/design/guard/guard-4-big.png') no-repeat 5px;
    background-size: 90px;
}
#ranking-list .ranking-position {
    background-color: #235B39;
    border-radius: 25px;
    width: 40px;
    height: 40px;
    color: #fff;
    font-family: 'Banda bold', serif;
    font-size: 25px;
    font-weight: 700;
    text-align: center;
    line-height: 40px;
    margin: 1px auto;
}
#ranking-list .ranking-first {
    height: 108px;
    color: #235B39;
}
#news-list .news {
    background: #CCDBEF no-repeat 20px;
    position: relative;
    border-top: 2px solid #083779;
    height: 50px;
}
#index-news .date {
    float: left;
    background-color: #515379;
    width: 55px;
    height: 25px;
    color: #fff;
    border-radius: 10px;
    font-size: 16px;
    text-align: center;
    line-height: 25px;
    margin: 12px 5px;
}
#index-player {
    position: absolute;
    margin: 0 auto;
    width: 420px;
    height: 600px;
    right: 20px;
    top: 20px;
    border: solid 2px #10134A;
    border-radius: 10px;
    overflow: hidden;
    background-color: #fff;
}
/*PROFILE*/
#right-profile-tab {
    float: left;
    width: 369px;
    padding: 15px 15px 30px;
    margin: 10px 0 0 10px;
    background-color: rgba(255, 255, 255, 0.5);
    border-radius: 10px;
    position: relative;
    overflow: auto;
    min-height: 111px;
}
.flavr-button, .button.button {
    background-color: #545A80;
    border: 2px solid #545A80;
    border-radius: 15px;
    font-family: Banda Semi Bold, sans-serif;
    font-size: 14px;
    color: #fff;
    padding: 4px 8px;
    cursor: pointer;
    display: inline-block;
    line-height: 100%;
    -webkit-transition: background-color 0.3s ease-in, color 0.3s ease-in;
    -moz-transition: background-color 0.3s ease-in, color 0.3s ease-in;
    -ms-transition: background-color 0.3s ease-in, color 0.3s ease-in;
    -o-transition: background-color 0.3s ease-in, color 0.3s ease-in;
    transition: background-color 0.3s ease-in, color 0.3s ease-in;
    margin: 0 5px;
}
#player-left-tab {
    float: left;
    margin: 10px 10px 0 94px;
    width: 370px;
    padding: 10px;
    background-color: #545A80;
    border-radius: 10px;
    position: relative;
}
/*FAMILIER*/
#pet-info-food, #pet-info-levels {
    background: #78788A;
    border-radius: 10px;
    margin: 15px 0;
    padding: 15px;
    color: rgb(52, 56, 111);
    font-weight: bold;
    overflow: hidden;
}
#pet-info-main {
    padding: 15px;
    background-color: #78788A;
    border-radius: 10px;
    position: relative;
}
#pet-info-racegender, #pet-info-age {
    font-size: 14px;
    color: #10134A;
    margin: 6px 0 0;
}
#pet-info-name {
    font-size: 20px;
    color: #10134A;
    font-family: "Banda Regular", serif;
    max-width: 240px;
    line-height: 16px;
}
#pet-info-energy-level, #event-bonus-progressbar-fill {
    background-color: #9AE993;
    position: absolute;
    left: 0;
    top: 0;
    height: 25px;
    -webkit-border-radius: 10px;
    -moz-border-radius: 10px;
    border-radius: 10px;
    max-width: 100%;
}
#pet-info-evolve div.on {
    color: #10134A;
    font-weight: bold;
}
#pet-info-food img {
    float: left;
    width: 70px;
    height: 70px;
    border-radius: 50px;
    border: 2px solid #10134A;
    background: #FEEDDE;
}
#pet-info-affection-level div {
    height: 29px;
    background: url("http://nsa37.casimages.com/img/2016/05/08/160508055456396687.png");
}
#pet-info-affection-level {
    display: inline-block;
    width: 175px;
    height: 29px;
    background: url("http://nsa37.casimages.com/img/2016/05/11/160511011505575666.png");
}
#pet-info-luck-level {
    display: inline-block;
    width: 30px;
    min-height: 30px;
    padding-left: 35px;
    margin-left: 6px;
    background: url("http://www.eldarya.fr/static/img/pet/luck_full.png") 0% 50% no-repeat;
}
#main-bonus-progressbar > div.open > .label, #main-bonus-progressbar > div.transition_bonus.open {
    background-color: #9AE993;
}
#container h1 .title-end {
    background-image: url("http://www.eldarya.fr/static/img/design/h1-end.png");
    position: absolute;
    right: -25px;
    top: 0;
    width: 25px;
    height: 45px;
}
#pet-info-evolve div {
    font-size: 13px;
    color: white;
    margin: 7px 0 0;
}
/*MINIJEU*/
.flavr-button.purple, .button.button.button-purple {
    background-color: #78788A;
    border-color: #78788A;
}
/*BIBLI*/
#library-container li {
    background-color: rgba(255, 255, 255, 0.5);
    min-height: 100px;
    padding: 10px;
    border: 1px solid #ccc;
    border-radius: 10px;
}
/*MAIL*/
#messaging-conversation-content-bloc {
    position: relative;
    box-shadow: 0px -2px 3px rgba(52,56,111,0.5);
    background-color: rgba(255, 255, 255, 0.5);
    border-radius: 5px;
    height: 50px;
    padding: 10px;
}
/*FORUM*/
.pun .author {
    background-image: url(http://nsa38.casimages.com/img/2016/05/08/160508061002652067.png);
    color: #333;
    float: left;
    font-size: 11px;
    padding: 0px;
    width: 170px;
}
#forum .pun.box > .inbox .author .author_title strong {
    margin-top: 5px;
    clear: both;
    font-size: 11px;
    display: block;
    text-align: center;
    color: #10134A;
}
/*GEMS*/
.guard-gem.guard-4 {
    background-image: url("http://img15.hostingpics.net/pics/244956752856markerombre02.png")!important;
}

.guard-gem.guard-2 {
    background-image: url("http://img15.hostingpics.net/pics/988564864579markerobsidienne02.png")!important;
}


.guard-gem.guard-3 {
    background-image: url("http://img15.hostingpics.net/pics/546236519141markerabsynthe02.png")!important;
}


.guard-gem.guard-1 {
    background-image: url("http://img15.hostingpics.net/pics/219865133836markeretincelant02.png")!important;
}
/*LUCIOLES*/
.flavr-container .flavr-fixer .flavr-outer .bL {
    background-image: url('http://nsa38.casimages.com/img/2016/05/08/160508062408950698.png');
    bottom: -70px;
    left: -150px;
    width: 205px;
    height: 220px;
}
.flavr-container .flavr-fixer .flavr-outer .tL {
    background-image: url('http://nsa37.casimages.com/img/2016/05/08/160508063117728292.png');
    top: -70px;
    left: -65px;
    width: 83px;
    height: 124px;
}
.flavr-container .flavr-fixer .flavr-outer .tR {
    background-image: url('http://nsa37.casimages.com/img/2016/05/11/160511011507389983.png');
    top: -190px;
    right: -110px;
    width: 203px;
    height: 195px;
}
.flavr-container .flavr-fixer .flavr-outer .bR {
    background-image: url('http://nsa38.casimages.com/img/2016/05/08/160508063118866499.png');
    bottom: -90px;
    right: -80px;
    width: 86px;
    height: 147px;
}
/*DISCONNECT*/
#punchline .rightLights {
    right: -160px;
    background-image: url('http://nsa37.casimages.com/img/2016/05/08/160508063933901649.png');
}
#punchline .leftLights {
    left: -160px;
    background-image: url('http://nsa38.casimages.com/img/2016/05/08/160508063933101717.png');
}
}
