    background-color: rgba(0, 0, 0, 1.0) !important;
    border-color: rgba(0, 0, 0, 1.0) !important;
}

.sc-button-play  {
    background-color:#A662BF;
    border-color:rgba(0, 0, 0, 0.0) !important;
    transition: all .2s ease-in-out;
}
