    transition: all .1s ease-in-out;
    border-radius:5px;
}

.compactTrackListItem {
	transition: all .1s ease-in-out;
}
.playButton.m-stretch, .playButton.m-stretch:hover {
    background:#A662BF;
}
.playButton.m-stretch:hover {
    background:#9160A3;
}
@keyframes badge {
    from {opacity: 0.0}
    to {opacity: 1.0}
}

.sc-button-play:hover, .sc-button-play:focus, .sc-button-play.sc-button-focus, .sc-button-pause:hover, .sc-button-pause:focus, .sc-button-pause.sc-button-focus, .sc-button-prev:hover, .sc-button-prev:focus, .sc-button-prev.sc-button-focus, .sc-button-next:hover, .sc-button-next:focus, .sc-button-next.sc-button-focus {
