style.css:
@-moz-document domain("git.epicsparrow.com") {
:root{

	--light-background: #aaa;
	--light-color:      #282828;

	--dark-background:  #303030;
	--dark-color:       #a0a0a0;

	--enlight-color:    /*[[#6EAAE8]]*/;

	--clear-color:      white;
	--clear-background: #454545;
	--black-background: #202020;

	--weird-color      : #FFE68D;
	--weird-background : #5F4A00;
}

.select2-search input.select2-active{
	background-image:none!important;
}
  /******************************************************/
 /*           redefine default behavior                */
/******************************************************/
small,
.cover-block,
.cover-title,
body,
body.navless{
	background-color: var(--clear-background) !important;
	color: var(--clear-color) !important;
	border-bottom:none;
	margin-bottom:1px;
}

h1,h2,h3,h4,h5,h6 {
	color: var(--clear-color);
}

a,a:hover{
	color: var(--enlight-color);
}

.breadcrumb a{
	color: #b5b5b5!important;
}
/*gray links on hover*/
.pull-right:hover,
.sidebar-collapsed-icon:hover,
.sidebar-collapsed-icon:hover > .fa,

/*blue links*/

.author_link *,
.commit-info-row a,
a.commit_short_id,
.light-well a,
.content-block a:not(.btn),
.content-list a:not(.text-expander):not(.btn),
.row-content-block p > a,
.event-item a,
.pull-right:hover > i.fa,
.item-title a:not(.text-expander),
.commit-row-info > a{
	color: var(--enlight-color)!important;
	text-decoration:none;
	background-color:transparent!important;
}

.light-well a:hover,
.content-block a:hover,
.content-list a:hover:not(.text-expander):not(.btn),
.row-content-block p > a:hover,
.event-item a:hover,
.item-title a:hover,
.commit-row-info a:hover{
	text-decoration:underline;
}

.arrow {
	display: none;
}

.content-block{
	border:none;
	padding:0!important;
}

  /******************************************************/
 /*           navbar on top                            */
/******************************************************/

.header-content a,
.navbar-toggle{
	background-color:transparent!important;
	color:var(--dark-color)!important;
}

.header-content .search-icon,
.header-content .fa,
.fa-navicon:before,
.fa-reorder:before,
.fa-bars:before{
	color:var(--dark-color)!important;
}

header.navbar-gitlab .side-nav-toggle,
header.navbar-gitlab .side-nav-toggle:hover{
	background-color:transparent;
}

header.navbar-gitlab,
header.navbar-empty{
	background-color: var(--dark-background) !important;
/*	box-shadow: 0px 0px 4px 1px black;*/
	border-bottom: none !important;
}

/*shadow trick*/
.content-wrapper .container-fluid,.container-limited{
	max-width: 100%;
	padding: 0px 15px!important;
}
.page-with-sidebar{
	padding-bottom:0;
}

.container-fluid.container-limited{
	padding:0;
}
.content-wrapper > .container-fluid.container-limited,
.content-wrapper > div[class=" "] > .content{
	box-shadow: 0 0px 4px 0px black inset;
	min-height: 100vh;
}

.header-content .fa-chevron-down:hover,
.header-content a:hover .fa,
.header-content a:hover,
.fa-navicon:hover:before, .fa-reorder:hover:before, .fa-bars:hover:before{
	color:var(--enlight-color)!important;
}

.header-content .badge{
	color:var(--dark-background);
}

#search{
	background-color:transparent!important;
}

.search .navbar-form{
	padding-top:4px!important;
	padding-left:4px!important;
}

  /******************************************************/
 /*           behind ahead bar                         */
/******************************************************/

.count{
	color:var(--dark-color)!important;
}

.bar-behind{
	background-color:red!important;
}

.bar-ahead{
	background-color:green!important;
}

  /******************************************************/
 /*           left sidebar                             */
/******************************************************/

.sidebar-wrapper {
	background: var(--dark-background) !important;
}

.nav-sidebar li:hover *{
	color:var(--enlight-color)!important;
}

.nav-sidebar li.active *{
	color: var(--light-color) !important;
}

.nav-sidebar li.active .count{
	color:var(--enlight-color)!important;
}

.nav-sidebar li.active a {
	background: var(--enlight-color) !important;
}

  /******************************************************/
 /*           nav-links stuff under the top            */
/******************************************************/

.layout-nav{
	background-color:transparent!important;
	border : none;
}

.nav-links a{
	color:var(--dark-color)!important;
}

.nav-links a:hover,
.nav-links li.active a{
	color:var(--enlight-color)!important;
	border-bottom-color:var(--enlight-color)!important;
}

.nav-links li .badge{
	color:var(--light-color)!important;
}

.nav-links li.active .badge{
	background-color:var(--enlight-color)!important;
}

.container-fluid{
	padding:0px;
}

.nav-links.sub-nav{
	background-color:transparent;
}

.nav-links{
	padding-bottom:10px!important;
	background-color:transparent;
	color: var(--dark-color)!important;
	border:none!important;
	border-bottom-color: var(--dark-color)!important;
}

.nav-links.scrolling-tabs{
	background-color:var(--dark-background);
	box-shadow: 0px 0px 4px 1px black;
}

.activities .nav-block,
.top-area,
.detail-page-header,
.nav-block .controls{
	border:none;
}

div.nav-block > ul > li:hover > a{
	color:var(--enlight-color)!important;
}

  /******************************************************/
 /*           panel                                    */
/******************************************************/

.panel{
	border: none;
	border-radius:4px;
	box-shadow: 0px 0px 4px 1px black;
}

.panel,
.panel-heading{
	border: none;
	border-top-left-radius: 4px;
	border-top-right-radius: 4px;
	color:var(--dark-color)!important;
	background-color: var(--black-background) !important;
}

.panel .well-list,
.panel-body{
	background-color: var(--dark-background) !important;
	border-bottom-left-radius: 4px;
	border-bottom-right-radius: 4px;
}

.panel.panel-small .panel-heading a{
	color:var(--enlight-color)!important;
}


.panel .well-list li:hover{
	background-color:var(--dark-background);
}

  /******************************************************/
 /*                right sidebar                       */
/******************************************************/

.right-sidebar{
	background-color: var(--black-background)!important;
	box-shadow: 2px 2px 10px 0px black!important;
	border: none;
}

.right-sidebar,
.issuable-sidebar .title,
.right-sidebar .light,
.right-sidebar a:not(.issuable-pager),
.pull-right,
.sidebar-collapsed-icon,
.sidebar-collapsed-icon > .fa{
	color:var(--dark-color)!important;
}

.issuable-sidebar .title:hover *{
	color: var(--enlight-color)!important;
}

  /******************************************************/
 /*                buttons                             */
/******************************************************/

.text-expander,
button.dropdown-menu-toggle,
.btn,
.select2-choice,
.btn-file-option,
.project-stats a,
.content-block a.ci-status,
.count-with-arrow .count
{
	border: none!important;
	background-color: var(--light-background) !important;
	color: var(--light-color)!important;
	text-shadow: 1px 1px 0px hsla(0, 0%, 100%, 0.25);
	box-shadow: 
		 2px  2px  5px 0px hsla(0,0%,  0%,1   ),
		-2px -2px 10px 0px hsla(0,0%,  0%,0.25) inset,
		 2px  2px 10px 0px hsla(0,0%,100%,0.25) inset,
		 1px  1px  1px 0px hsla(0,0%,100%,0.5 ) inset,
		-1px -1px  1px 0px hsla(0,0%,  0%,0.25) inset;
	transition-duration:0.25s!important;
	text-decoration:none!important;
}

.text-expander:hover,
.open > button.dropdown-menu-toggle,
button.dropdown-menu-toggle:hover,
.select2-choice:hover,
.btn:hover,
.btn-file-option:hover,
.project-stats a:hover,
.content-block a.ci-status:hover,
.count-with-arrow:hover .count,

.text-expander:focus,
button .dropdown-menu-toggle:focus,
.select2-choice:focus,
.btn:focus,
.btn-file-option:focus,
.project-stats a:focus,
.content-block a.ci-status:focus,
.count-with-arrow:focus .count{
	box-shadow: 1px 1px 3px 2px hsla(0,0%,0%,0.5) inset, 1px 1px 0px 0px hsla(0,0%,100%,0.5) !important;
	text-decoration:none!important;

}

.btn-success,
.btn-create,
.submit {
	border-color: #2faa60!important;
	background-color: #2faa60 !important;
	text-decoration: none;
}

.btn-warning {
	border-color: #fc6d26!important;
	background-color: #fc6d26 !important;
}
.btn-default {
	border-color: #e5e5e5!important;
	background-color: #e5e5e5 !important;
}
.btn-remove {
	background-color: #F33!important;
	border-color: #F33!important;
}
.btn .light,
.select2-container b,
.btn i.fa,
a.btn,
.ci-status .fa,
button .fa{
	color:var(--light-color)!important;
}	

  /******************************************************/
 /*                alternative stye                    */
/******************************************************/
ul.notes .system-note,
.issuable-details,
.blob-commit-info,
.md-area,
.project-last-commit,
.event-item,
.issue,
.todo-done,
.todo-pending,
.content-list> .commit{
	color : var(--dark-color)!important;
	background-color: var(--dark-background) !Important;
	padding-right: 10px!important;
	border:none!important;
	border-radius: 4px;
	box-shadow: 0px 0px 10px 1px #000!important;
	margin-top:15px!important;
	margin-bottom: 15px!important;
}

.issuable-details,
.prepend-top-default{
	padding:15px;
}

.commit{
	padding-left:10px!important;
}

.project-last-commit {
	padding: 20px;
}
.event-item .avatar {
	margin-left: -40px!important;
}

.commit-row-info{
	color : var(--dark-color)!important;
}

  /******************************************************/
 /*                dropdown (menus daroulants)         */
/******************************************************/

.select2-results li.select2-result-with-children>.select2-result-label,
.dropdown-header,
.dropdown-menu a{
	color: var(--light-color)!important;	
}

ul.dropdown-menu,
.select2-drop,
div.dropdown-menu,
.select2-no-results{
	background-color: var(--light-background)!important;
	color: var(--light-color)!important;
	box-shadow: 2px 2px 10px 0px black!important;
}

.select2-searching{	
	background-color: var(--light-background)!important;
}

.commit-title,
.select2-drop-active
.select2-results
.select2-highlighted,
.dropdown-menu a:hover,
.dropdown-menu a:focus,
.dropdown-menu a.is-focused{
	background:var(--dark-background)!important;
	color:var(--dark-color)!important;
}

.dropdown-content{
	overflow-y: auto!important;
}

.btn .light,
.select2-container b,
.btn i.fa,
a.btn{
	color: var(--dark-background)!important;
}

.select2-container{
	background-color:transparent!important;
}

.dropdown-loading/*, i.fa-spinner*/{
	background:hsla(0,0%,65%,.5);
}

.account-well,
.input-group,
.profile-settings-message,
.input-group-addon {
	background-color: #333!important;
	border-color: #fff!important;
	color: #fff!important;
}

  /******************************************************/
 /*        status message                              */
/******************************************************/
.ci-status,
.location-badge,
.help-block {
	color: var(--weird-color)!important;
	background-color: var(--weird-background)!important;
	padding: 10px;
	border-radius: 4px;
}

  /******************************************************/
 /*        blocks                                      */
/******************************************************/

.dark,
.commit-row-description,
.commit-row-description pre,
.well,
.light-well,
.md *,
.bs-callout-warning,
.nothing-here-block{
	background-color: var(--dark-background)!important;
	color: var(--dark-color)!important;
	border:none!important;
}

.commit-row-description{
	padding-right:0px!important;
	padding-left:0px!important;
}

  /******************************************************/
 /*        blocks-with lines                           */
/******************************************************/

.row-content-block {
	background-color: var(--dark-background)!important;
	border-bottom: 1px solid var(--enlight-color)    !important;
	border-top: 1px solid var(--enlight-color) !important;
	color: var(--dark-color)!important;
	margin-bottom:20px!important;
}

  /******************************************************/
 /*        transparents - contents                     */
/******************************************************/

g{
	fill:var(--dark-color)!important;
}
canvas,svg{
	background-color:var(--dark-background)!important;
}

  /******************************************************/
 /*                 tables                             */
/******************************************************/


table > tbody > tr *{
	color: var(--dark-color)!important;
}

.table-striped tr.selected td,
table tr th,
table tr:hover *{
	background: var(--dark-background)!important;
	color:var(--enlight-color)!important;
}

.table-striped>tbody>tr{
	background-color:transparent!important;
}

.lists-separator,
.content-list>li,
.event-title,
table thead {
	color: var(--dark-color) !important;
	border-color: var(--enlight-color)!important;
}

table > tbody tr td{
	border-color: var(--enlight-color)!important;
}


  /******************************************************/
 /*                 input                              */
/******************************************************/

input[type=search]:not([id=search]),
.select2-choices,
textarea,
.form-control,
.search .navbar-form,
.dropdown-menu-toggle input
 {
	color: var(--enlight-color) !important;
	box-shadow: 0px 0px 4px 1px black inset!important;

	border: 1px solid #606060!important;
	background-color: var(--light-color)!important;
	border-radius: 0px!important;
}

.form-control.middle,
.form-control.bottom{
	margin-top:10px;
}

input.search-input{
	text-shadow:none!important;
}

input[type=search]:not([id=search]):focus,
.select2-choices:focus,
textarea:focus,
.form-control:focus,
.search-active .navbar-form,
.dropdown-menu-toggle input:focus{
	border: 1px solid var(--enlight-color)!important;
	box-shadow: 0 0 8px /*[[#6EAAE8]]*/, 0px 0px 4px 1px black inset!important;
/*	text-shadow: 0px 2px 0px black,0px 0px 1px black, 0px 0px 8px rgba(58,171,240,1)!important;*/
}

.dropdown-input .fa{
	color:var(--dark-color)!important;
}

textarea{
	padding:10px!important;
}

  /******************************************************/
 /*                 search                             */
/******************************************************/

.select2-search > input,
.search-input {
	background-color: var(--dark-background) !important;
	color: var(--enlight-color);
}

  /******************************************************/
 /*                 random                             */
/******************************************************/


.well-list>li:hover {
	background: gray; /* TODO */
}

.commit-box,
.project-edit-content,
.panel-small,
.panel-small>.panel-heading,
.panel-small>.panel-body{
	background-color:transparent!important;
	box-shadow: none;
	color:white!important;
	border:none;
}

.content-list p,
.cgray,
.label.label-gray,
legend {
	background-color:transparent;
	color: #b5b5b5!important;
	border-bottom-color: #b5b5b5;
}

table,
.wiki table thead th,
.file-holder,
.comment-toolbar{
	border:none!important;
}

.js-todos-list .panel-heading,
.js-todos-list,
.js-todos-list panel{
	background-color:transparent!important;
	box-shadow:none!important;
}

/*ta partie*/

.badge{
	background-color:#aaa!important;
}

a:hover>span.badge,
a:active>span.badge,
a:focus>span.badge {
	background-color: var(--enlight-color) !important;
}

.select2-container-multi .select2-choices .select2-search-field input.select2-active{
	background: #282828 none !important;
	border:none!important;
}


  /******************************************************/
 /*                no-idea                             */
/******************************************************/

.project-network .controls {
	background: #2B2B2B !important;
}

.login-page .login-box{
	background-color: #303030 !important;
	box-shadow: 0px 0px 4px 1px #000;
	border-bottom: none !important;
}
body.ui_charcoal .page-with-sidebar .header-logo {
	background-color: #303030 !important;
	border-color: #303030 !important;
}

.sub-header-block, .content-wrapper .container-fluid {
	background: transparent !important;
}

tbody .fa {
	box-shadow: inset 0px 0px 34px 8px rgba(0, 0, 0, 0) !important;
	border-radius: 2px;
	padding: 7px;
	margin-right: 10px;
	width: 30px;
}

.git-empty h5 {
	color: #fff !important;
}
.detail-page-description .title {
	color: #fff !important;
}
.issuable-sidebar a {
	color: #4185ef;
}
.detail-page-header .identifier {
	color: #FFFFFF;
}
.issuable-meta {
	color: #cccccc;
}
.detail-page-header .author {
	color: #4185ef !important;
}
.form-actions {
	background: transparent !important;
	border-top: none !important;
}
.wiki,
.wiki h2 {
	color: #fff;
}
.wiki p,
.wiki table {
	color: #BBBBBB;
	border:none;
}

.wiki td{
	border-right:transparent!important;
	border-left:transparent!important;

}

.cover-block .cover-desc,
.cover-title h1{
	color: #dddddd !important;
}

.project-stats li.missing a:hover {
	background-color: #000000;
}
.prepend-top-default .content-block {
	color: #4185ef;
	background-color: #303030;
}
.light {
	color: #fff;
}
.file-holder .file-content {
	background: #303030;
}
.file-holder .file-title {
	color: #fff;
	border-bottom: none;
	background-color: #4185ef;
}

.time_ago{
	color:white!important;
}

.file-holder .file-title a:not(.btn) {
	color: #fff;
}
.file-holder.readme-holder {
	margin-top: 5%;
}
table.table tr th {
	background-color: #303030;
	border: none;
}
.breadcrumb.repo-breadcrumb a {
	color: #4185ef;
}
.blob-commit-info {
	background: transparent;
}
body.ui_charcoal .page-with-sidebar .sidebar-wrapper .sidebar-user {
	background: #000;
	color: #fff;
}
body.ui_charcoal .page-with-sidebar .collapse-nav a {
	color: #fff;
	background: #202020;
}
.panel admin-filter {
	background: transparent;
}
.merge-request.today,
.issue.today {
	background: transparent;
}
ul.notes .note .note-body .note-text p {
	color: #fff;
}
.note-header .author_link {
	color: #4185ef;
}
ul.notes .system-note {
	background: #202020;
}
.slead {
	color: #BBBBBB;
}

.option-title {
	color: #CBCEDC !important;
}
.option-descr {
	color: #848484 !important;
}
.arrow:before,
.arrow:after {
	border-right-color: #282828 !important;
}
.wiki h1 {
	color: #81BFFF;
}

.calendar-hint{
	margin-top:0px;
}
i.fa.fa-comment-o{
	background:transparent!important;
}
}
