style.css:
@-moz-document domain("arstechnica.com") {
img {
    mix-blend-mode: normal;
}
html > body div {
    color: white;
    background-color: black;
}
figure.intro-image figcaption {
    background-color: black;
}
article.single .post-lowerdek,
article.single .post-title {
    color: white;
}
figure div,
figure div:after {
    background-color: transparent;
    background-blend-mode: normal;
}
.post-tease .post-meta
{
    background-image: none;
    background-color: rgba(30, 30, 30, 0.87);
}
.post-tease .post-meta * {
    background-color: transparent;
}
.comments-row #comments-area #comments>li>.body .quotecontent, .comments-row #comments-area #comments>li>.body .quotetitle {
    background-color: rgba(128, 128, 128, 0.3);
    border-left-color: #fa501e;
    color: #AAA;
}

.quotetitle a,
.quotecontent a {
    background-color: transparent;
}
a {
    color: #fa501e !important;
    font-weight: normal;
}
body {
    background-color: black;
}
.row,
main {
    background-color: #181818;
}
section.xrail {
    display: none;
}
.comments-row #comments-area {
    margin-left: 100px;
    margin-right: 100px;
    width: auto;
}
.comments-row #comments-area #comments>li {
    padding: 25px;
    border-top: 1px solid #555;
}
}
