style.css:
@-moz-document domain("komixxy.pl") {
/*[[m]]*/
body {
    background: #262626 url('https://i.imgur.com/aYkucHN.png') repeat;
    color: #FFF;
    background-color: #262626;
}
#page-header > nav > h1 > a {
    background: url('https://i.imgur.com/I2gtRkp.png') no-repeat;
}
#submenu-wrapper {
    background-color: #1C1C1C;
    border-bottom: 1px solid #000000;
}
#page-header {
    width: 100%;
    height: 45px;
    text-align: center;
    border-bottom: 1px solid #000000;
    background-color: #262626;
}
#main-menu > li > a {
    background-image: url(https://i.imgur.com/sGw9qln.png);
}
.submenu > li > a {
    background-image: url(https://i.imgur.com/sGw9qln.png);
}
#main-menu li.expand > span:before {
    background: url(https://i.imgur.com/o0E7eKi.png) no-repeat -9999px -9999px;
}
#rss {
    background: none;
}
#main-menu > li.expand > div {
    background: rgb(28, 28, 28);
    box-shadow: 1px 1px 2px rgb(0, 0, 0);
}
ul#main-menu > li.visible {
    background: rgb(28, 28, 28);
    box-shadow: 1px 4px 4px #000000;
}
ul#main-menu > li.expand:not(#msearch):hover {
    background: #1C1C1C;
    box-shadow: 1px 4px 4px #000000;
}
ul#main-menu label:before {
    color: #F6F6F6;
}
H1, h2, .in_profile.user_title, a, a:hover ,#footer a {
    color: #FFF;
}
div.pic {
    border: 1px solid #000;
    -moz-box-shadow: 3px 3px 3px #000;
    -webkit-box-shadow: 3px 3px 3px #000;
    background: #1C1C1C;
    padding: 5px;
    margin: 2px auto;
}
div.infobar {
    padding: 0px;
    background: #1C1C1C;
    text-align: justify;
    color: #FFF;
}
.list_next_page_button {
    color: #09f;
    background-image: -webkit-linear-gradient(top, #000000, rgb(32, 32, 32));
    border: 1px solid #000;
}
div.comment, .reply {
    background: #2C2C2C;
}
p.commcontent {
    background: #2C2C2C none repeat scroll 0 0;
    color: #FFF;
}
.paginator span strong, .paginator span a {
    background-color: #000000 !important;
}
.paginator span strong {
    background: #000000 !important;
    color: #09F !important;
}
.paginator span a:hover {
	color: #fff !important;
    border-color: #0099F7 !important;
}
.paginator .current_page_mark {
    background: #0099FF !important;
}
div.comminfo {
    color: #C8C8C8;
}
.pretty_button:hover {
    background-color: #222222;
}
.pretty_button {
    border: 1px solid black;
    background-color: #282828;
    -moz-box-shadow: 2px 2px 2px #888;
    box-shadow: 2px 2px 2px #000;
}
#top_block {
    background: #1C1C1C;
}
#top_block .pics .header .title, #top_block .author .header .title, #top_block .via_fb .header .title, .box_widget .title{
    border: 1px solid #000;
    box-shadow: 0px 2px 3px #000;
    background: linear-gradient(to bottom, rgb(36, 36, 36) 0%,rgb(44, 44, 44) 50%,rgb(44, 44, 44) 51%,rgb(36, 36, 36) 100%);
}
.series_button {
    background-color: #191919;
}
#footer {
    background: #1C1C1C;
    color: #FFF;
    border: 1px solid #000;
}
#comments_placeholder > div:nth-child(4) {
    color: #222!important;
    border: 1px solid #000!important;
    background: #0f0f0f!important;
    box-shadow: 0 2px 13px #000!important;
}
#profileinfobox {
    border: 1px solid #000;
}
table#profileinfo td {
    border-bottom: 1px dotted #000;
}
.hidden_bar {
    background: #2c2c2c;
}
.hidden_bar_text {
    color: #ffffff;
}
}
