@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
@-moz-document url("about:Home"), url("about:home") {
  @font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v13/cJZKeOuBrn4kERxqtaUH3VtXRa8TVwTICgirnJhmVJw.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000; }
  
  html {
    font: message-box;
    font-size: 100%;
    color: #808080;
  }
  body {
    background-color:#353d45!important;
    margin: 0;
    display: -moz-box;
    -moz-box-orient: vertical;
    width: 100%;
    height: 100%;
    font-family: 'Open Sans', sans-serif!important;
  }

  #searchText {
    
    color: #808080!important;
    background-color:#2b303b !important;
    margin-left: 0;
    -moz-box-flex: 1;
    padding-top: 6px;
    padding-bottom: 6px;
    padding-left: 34px;
    padding-right: 8px;
    background: none;
    border: none!important;
    border-radius: 4px 0 0 4px;
    border-color: transparent!important;
    box-shadow: none!important;
    color: inherit;
    font-family: 'Open Sans', sans-serif!important;
  }
  #searchText:focus + #searchSubmit,
  #searchText[keepfocus] + #searchSubmit,
  #searchText + #searchSubmit:hover,
  #searchText[autofocus] + #searchSubmit {
    border-color: #2b303b;
    color: white;
  }

  #searchText:focus + #searchSubmit,
  #searchText[keepfocus] + #searchSubmit,
  #searchText[autofocus] + #searchSubmit {
    background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat, #2b303b!important;
    box-shadow: none!important;
  }
  #searchSubmit {
    -moz-margin-start: -1px;
    background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go") center center no-repeat;
    background-color: #2b303b;
    padding: 0;
    border: none;
    border-color: transparent;
    border-radius: 0 2px 2px 0;
    -moz-border-start: none;
    box-shadow: none;
    color: inherit;
  }
  #searchSubmit:-moz-dir(rtl) {
    border-radius: 2px 0 0 2px;
  }

  #searchText + #searchSubmit:hover {
    background: url("chrome://browser/skin/search-arrow-go.svg#search-arrow-go-inverted") center center no-repeat, #22262f!important;
    box-shadow: none!important;
  }

  #searchText + #searchSubmit:hover:active {
    box-shadow: none;
    transition-duration: 0ms;
  }
  #brandLogo {
    background-image: url("http://s32.postimg.org/4g9kwz7it/firefox_logo.png") !important;
  }
  div#snippetContainer {
    visibility:hidden;
  }
  #launcher {
  /*display: none;*/
    border: 0px transparent !important;
    background-color: transparent !important;
    box-shadow: none !important;
    margin:0px!important;
  }
  .launchButton {
    color: transparent !important;
    margin:0px!important;
    border-radius: 0px!important;
  }

  .launchButton:hover {
    background-color: #353d45;
    border-color: 0px transparent;
    border-bottom: 6px solid #E2671E!important;
  }
  .launchButton:hover:active {
    background-image: none!important;
    background-color: #3d4349 !important;
    border-bottom: 4px solid #E2671E!important;
    box-shadow: none !important;
    transition-duration: 0ms;
  }
  #restorePreviousSessionSeparator {
    visibility: hidden !important;
    width:0px!important;
    height:0px!important;
  }
  #restorePreviousSession {
    visibility: hidden;
    width:0px!important;
    height:0px!important; 
    margin: 0px 0px 0 0!important;
    padding: 0px 0px 0 0!important;
    min-width:0px!important;
    min-height: 0px!important;
    display: none!important;
  }
  #downloads::before {
    content: url("http://s33.postimg.org/7e157vq6n/download_from_cloud.png")!important;}
  #bookmarks::before {
    content: url("http://s33.postimg.org/4bsfyc0gf/star.png")!important;}
  #history::before {
    content: url("http://s33.postimg.org/plnvejgpb/clock.png")!important;}
  #addons::before {
    content: url("http://s33.postimg.org/5qizq2r0v/add_on.png")!important;}
  #sync::before {
    content: url("http://s33.postimg.org/ce7s7bakv/refresh.png")!important;}
  #settings::before {
    content: url("http://s33.postimg.org/ugqr4atsf/cogwheel.png")!important;}
  
   /* MOZILLA */
  #aboutMozilla {
    opacity: 1;
  }   
  #aboutMozilla::before {
    font-family: 'Open Sans', sans-serif!important;
    font-size: 18px !important;
    color: #aaaaaa !important;
    content: "Mozilla" !important;
    transition-property: color;
    transition-duration: 800ms;
  }
  #aboutMozilla:hover:before {
    font-family: 'Open Sans', sans-serif!important;
    font-size: 17px !important;
    color: #FF9500 !important;
    content: "Rush4Art" !important;
    text-align:center;
  }
}
