style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https?://jen(kins)?\\..*'), domain('ci.jenkins.io') {
  td, th, div, body, p, input {
    font-family: Calibri, Helvetica, sans-serif;
  }
  
  .tabBar .tab:first-child {
    margin-left: 5px;
  }  
  .tabBar .tab a {
    padding: 2px 4px;
    border: solid 1px #aaa !important;
    /*
    xborder-bottom: 0 !important;
    */
    border-bottom: solid 1px #aaa !important;
    position: relative;
  }
  
  .tabBar .tab.active a {
    border-bottom: solid 1px #eee !important;
  }
  
  .dashboard {
    position: relative;
  }
  
  .dashboard #projectstatus-tabBar {
    position: relative;
    top: 1px;
    z-index:1;
  }
  .dashboard #projectstatus {
    position: relative;
    z-index:0;
  }
  
  #projectstatus {
    border: solid 1px #aaa !important;
  }
  
  img.icon-lg, img.icon-md {
    width: 16px !important;
    height: 16px !important;
  }
  
  .bigtable td {
    padding: 2px 4px;
  }
  
  #projectstatus th:nth-child(1) {
    width:24px;
  }
  
  #projectstatus th:nth-child(2) {
    width:24px;
  }
  
  #breadcrumbBar > div:nth-child(3),
  #right-top-nav,
  #breadcrumbs,
  #breadcrumbs LI {
    height: 1.3em;
  }
  
  #right-top-nav,
  #breadcrumbs LI,
  #breadcrumbs LI A {
    line-height: 1.3em;
    
  }
  
  .pane-header, .pane-footer {
    padding-top: 3px;
    padding-bottom: 3px;
  }
  
  #executors th.pane {
    padding-top: 3px;
    padding-bottom: 3px;
  }
  #executors td.pane {
    padding-top: 1px;
    padding-bottom: 1px;
  }
  
}
