style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wawision.biz") {
  
/*++++++++++ BASIC  ++++++++++ */  
  
/* KEINE WEIßEN RÄNDER */ 
  .grid_1, .grid_2, .grid_3, .grid_4, .grid_5, .grid_6 {
    margin-left: 0;
    width: 100%;
  }
  .container_6 .grid_6 {
    width: 100%;
  }

  
/* BODY AUßERHALB HAT SELBE FARBE */
  body {
    background: #48494B;
  }
  
  
/*  HEADER TIEFER */
  #jsddm {
    top: 64px;
  }
  DIV#header {
    height: 90px;
  }

  
/* HEADER 'NICHT IN ARBEIT' MEHR PADDING */
  div.nachrichtenbox {
    padding-bottom: 4px !important;
  }
  

/*  KEIN BORDER-BOTTOM FÜR DASHBOARD ICON */
  #jsddm {
    border-bottom: 0px solid #00c !important;
    border-right: 0px !important;
    border-left: 0px !important;
  }
  #jsddm li:nth-child(1){
    border-right: 0px !important;
    width: 45px !important;
  }
  
  
/*  HOVER FARBE VON SIDEBAR TEXT */
  .toolbarleftInner a:hover {
    color: #fff !important;;
  }
  
  
/* INPUT & TEXTAREAS BORDER RADIUS */
  input[type="text"], textarea {
    border-radius: 3px !important;
  }  
  
    
/* ALLE MITTELGRAUEN BOXEN */
  td[style="background-color:#ECECEC"], fieldset{
    border-radius: 5px !important;
  }
  

/* UNTERE TABS HOVER & BORDER */
  div[style="position:absolute;top:51px;"] a{
    border-radius: 3px 3px 0 0 !important;
  }
  div[style="position:absolute;top:51px;"] a:hover {
    background: #C2E3EA !important;
    color: #018FA3 !important;
  }
  
  
/* STARTSEITE FIELDS: BACKGROUND + BORDERS */
  
  /* fieldset {
     background-color: #02A8BF !important;
    border-color: #02A8BF !important; 
  }
   legend, .fc-header-title h2 {
    color: #eee !important;
  } */
  
  fieldset div {
    border-radius: 5px !important;
  }
  
  
  
  
  
  

}
