/********** Various fixes **********/
/* Set the font size to something more readable */

html {
    font-size: 20px !important;
}

/* Remove background and borders */

body,
.mw-body,
#mw-page-base,
.infobox,
.thumbinner,
#ca-nstab-main,
#ca-nstab-main span #ca-talk,
#ca-talk span,
#ca-viewsource,
#ca-viewsource span,
#ca-history,
#ca-history span,
#ca-edit,
#ca-edit span,
#ca-ve-edit,
#ca-ve-edit span,
#toc,
#simpleSearch,
.vectorTabs,
.vectorTabs *,
h1,
h2,
h3,
.inhalt,
.portal {
    background: none !important;
    border: none !important;
}

::selection {
  background: #bddfff; /* WebKit/Blink Browsers */
}
::-moz-selection {
  background: #bddfff; /* Gecko Browsers */
}

/********** Links **********/

a, .mw-body a.external {
    color: #268bd2;
    color: #2393e8;
    transition: color .1s ease-in-out;
    transition-property: color;
    transition-duration: 0.1s;
    transition-timing-function: ease-in-out;
    transition-delay: initial;
}

a:hover, .mw-body a.external:hover {
    text-decoration: none;
    /* color: #22bbbb; */
    color: #33bbff;
}

a:active {
    text-decoration: none;
    color: inherit;
}

a:visited {
    color: #268bd2;
}

#mw-head a,
#mw-panel a {
    color: #515151 !important;
}

#mw-head a:hover,
#mw-panel a:hover {
    text-decoration: underline;
}

/*********** Fonts **********/

/* PT Sans Narrow Bold */

h2,
h3,
#firstHeading {
    font-family: "PT Sans Narrow", "Helvetica", "Arial", sans-serif !important;
    font-weight: bold;
}

#mw-panel {
    font-family: "PT Sans Narrow", "Helvetica", "Arial", sans-serif !important;
    font-size: 1.1rem !important;
}

/* PT Serif */

body {
    font-family: "PT Serif", "Verdana", serif !important;
    text-rendering: optimizeLegibility;
}

/* Teko */
#mw-head {
    font-family: "Teko", "Helvetica", "Arial", sans-serif !important;
    font-weight: 400;
    font-size: 1.4rem;
}

/********** Head **********/

#mw-head {
    max-width: 44rem !important;
    
    margin-left: auto;
    margin-right: auto;
    left: 10rem !important;
    
    padding: 0 2rem 1rem 2rem !important;
    
   	/* box-shadow: 1rem -1rem 1rem -1rem #e8e8e8 , -1rem -1rem 1rem -1rem #e8e8e8; */
    box-shadow: 0px 0px 8px rgba(0, 0, 0, .1);
    
    /* background: #50585C !important; */
    background: #40484C !important;
    color: #fff !important;
}

#mw-head a, #mw-head * {
    color: inherit !important;
}

#p-personal{
    position: absolute;
	right: 2rem;
}

#left-navigation {
    margin-left: 0 !important;
}

#right-navigation, #left-navigation {
	margin-top: 2.5rem;
    margin-bottom: -0.7rem;
}

#p-views li, #p-namespaces li, #p-cactions {
    margin-top: .8em;
    vertical-align: bottom !important;
    height: 1em !important;
}

#p-views a, #p-namespaces a, #p-cactions span { 
    height: 1em !important;
    
    padding: .25rem !important;
    margin-right: .5rem;
}

#p-search {
    margin-left: 0;
    margin-right: 0;
    margin-top: .55rem;
}

#simpleSearch {
    width: 12rem !important;
    padding: 0em;
    padding-bottom: .1rem;
    background: #fff !important;
    border-radius: .1rem;
    
    height: 1.2rem !important;
}

#simpleSearch input[name='search']{
    font-size: .65em !important;
    color: #555 !important;
    
