@-moz-document domain("heise.de") {
    /* ====================================
       VARIABLES
       ==================================== */

    :root
    {
      --color-accent: rgb(18, 143, 231);
      --color-accent-red: rgb(189, 43, 73);
      --color-accent-tp-accent: rgb(237, 30, 72);
      --color-accent-tp-extra-translucent-50: rgba(237, 30, 72, 0.5);
      --color-accent-tp: rgb(182, 39, 0);
      --color-accent-light: rgb(105, 200, 255);
      --color-accent-alternate: rgb(137, 234, 80);
      --color-dark: rgb(80, 80, 80);
      --color-darker: rgb(60, 60, 60);
      --color-darker-translucent-80: rgba(40, 40, 40, 0.80);
      --color-darker-translucent-90: rgba(40, 40, 40, 0.90);
      --color-darkest: rgb(30, 30, 30);
      --color-black: rgb(0, 0, 0);
      --color-black-translucent-50: rgba(0, 0, 0, 0.5);
      --color-light: rgb(190, 190, 190);
      --color-light-translucent-50: rgba(190, 190, 190, 0.5);
      --color-lighter: rgb(240, 240, 240);
      --color-lightest: rgb(250, 250, 250);
      --color-lightest-translucent-85: rgba(250, 250, 250, 0.85);
      --color-lightest-translucent-50: rgba(250, 250, 250, 0.50);
      --color-medium: rgb(120, 120, 120);
      --color-topnavigation-container: rgb(40, 40, 40);
      --color-topnavigation-hairline: rgb(20, 20, 20);
    }

    /* ====================================
       ELEMENTS
       ==================================== */

    html, html *
    {
        -webkit-font-smoothing: antialiased !important;
        -moz-osx-font-smoothing: grayscale !important;
    }

    html
    {
        background: var(--color-dark);
    }

    body
    {
        color: var(--color-light);
    }

    body,
    .article-page
    {
        background: var(--color-dark);
    }

    .mitte,
    #container_content
    {
        background-color: var(--color-darker);
    }

    body a,
    body a:visited
    {
        color: var(--color-accent);
    }

    .article-content > p,
    .article-content > ul li
    {
        color: var(--color-lightest);
    }

    .article-header
    {
        max-width: none;
        margin-right: 0;
    }

    .a-toc__text
    {
        color: var(--color-light);
    }

    .a-toc__text--link:hover,
    .a-toc__text--onepage:hover
    {
        color: var(--color-accent-light);
    }

    .a-toc__text--current
    {
        color: var(--color-lighter);
    }

    img#heisetopnavi_ho_logo_top_img,
    a.heisetopnavi_button
    {
        display: none !important;
    }

    .heisetopnavi_logo
    {
        top: 7px !important;
    }

    .channel-info-svn-logo,
    .a-teaser-header__logo,
    .news-source-logo,
    .admin_icon,
    .thread_shift
    {
        filter: invert(1000%) brightness(100000%) opacity(0.5);
    }

    .mac__header__nav__icon svg path,
    .socialmedia-stage__svg,
    .article-actions__icon path
    {
        fill: var(--color-lightest);
    }

    .socialmedia-stage__link
    {
           color: var(--color-lightest);
    }

    .socialmedia-stage,
    .stage:not(:last-child)
    {
        border-color: var(--color-dark);
    }

    .a-teaser-header
    {
        border-color: var(--color-light);
    }

    .heisetopnavi.v14
    {
        border-bottom: none !important;
    }

    .sitemap-group__header,
    .footer-imprint__item a,
    .a-teaser-header a:link,
    .minibuehnenteaser.stage-videos .stage-channel
    {
        color: var(--color-lightest) !important;
    }

    .sitemap__link,
    .archiv_woche_published-time,
    .a-teaser-header__heading
    {
        color: var(--color-light) !important;
    }

    #mitte_news a h2,
    #mitte_news article h2,
    .channelteaser a:first-child,
    aside.recommendations a header,
    .a-article-teaser__content-container .a-article-teaser__title-text,
    .a-article-teaser__link-list__link,
    .a-teaser-header__more-text,
    .mac__teaser__item__titel,
    .akwa-article-teaser__title,
    #pre-akwa.ho .a-article-teaser__link-list__link,
    .heisetopnavi.v14 #navi_login a:link,
    .heisetopnavi.v14 #navi_login a:visited,
    .sidenav__link,
    .sidenav__link:visited,
    .account__section a,
    .account__section a:visited,
    .account__list .account__list-link,
    .account__list .account__list-link:visited,
    #forum_wrap a,
    .mail_on_reply_toggle.active::before,
    .mail_on_reply_toggle::before,
    #forum_wrap .paginierung_thread .active, #forum_wrap .paginierung_thread .newer, #forum_wrap .paginierung_thread .next_dummy, #forum_wrap .paginierung_thread .older, #forum_wrap .paginierung_thread .page, #forum_wrap .paginierung_thread .prev_dummy, #forum_wrap .paginierung_thread .vor, #forum_wrap .paginierung_thread .zurueck, #forum_wrap .paginierung_thread a, #forum_wrap .paginierung_thread em, #paginierung .active, #paginierung .newer, #paginierung .next_dummy, #paginierung .older, #paginierung .page, #paginierung .prev_dummy, #paginierung .vor, #paginierung .zurueck, #paginierung a, #paginierung em, .paginierung .active, .paginierung .newer, .paginierung .next_dummy, .paginierung .older, .paginierung .page, .paginierung .prev_dummy, .paginierung .vor, .paginierung .zurueck, .paginierung a, .paginierung em,
    .a-inline-textbox__content-container a
    {
        color: var(--color-accent);
        transition: color 100ms ease-in-out;
    }

    .a-article-teaser__link:hover .a-article-teaser__title-text,
    .a-article-teaser__link-list__link:hover,
    .heisetopnavi.v14 #navi_login a:hover,
    .sidenav__link:hover,
    .account__list .account__list-link:hover,
