    {
        color: var(--color-accent-light) !important;
    }

    .mac__teaser__item__text
    {
        color: var(--color-accent);
    }

    .minibuehnenteaser.stage-videos a
    {
        color: var(--color-light) !important;
    }

    .kommentare,
    .channelteaser .media .comments,
    .article__heading,
    .beitraganriss,
    .a-teaser-header__slug,
    .article_headline,
    .archiv-liste__text--text
    {
        color: var(--color-lightest) !important;
    }

    .dossier,
    .heisebox,
    .heisebox_left,
    .heisebox_right
    {
        background-color: var(--color-darkest) !important;
        border: none !important;
    }

    .shariff .info a,
    .shariff .mail a,
    .shariff .twitter a,
    .shariff .facebook a,
    .shariff .googleplus a
    {
        background-color: var(--color-darkest) !important;
    }

    .link_forum_beitrag p a,
    #mitte_news .themen_foren a,
    .article-content p a,
    .beitragsfooter a,
    .article-footer__comments,
    .article-footer__related-forum a,
    .article-footer__permalink a,
    .akwa-article__content > p a
    {
