        color: var(--color-light) !important;
    }

    .paginierung a:link,
    .paginierung a:visited
    {
        color: var(--color-light) !important;
    }

    .paginierung .page,
    .paginierung em
    {
        color: var(--color-m) !important;
    }

    time.mark-red-24h
    {
        color: var(--color-light) !important;
    }

    .themenseiten .themen_label
