    }

    .recommendation time
    {
        color: var(--color-lightest) !important;
    }

    .bbcode_v1 p a
    {
        color: var(--color-accent) !important;
        text-decoration: underline !important;
    }

    .bbcode_v1 p
    {
        color: var(--color-light) !important;
    }

    #tree_thread_list .posting_subject:link,
    .thread_list--posting_element .latest_posting_reply_info span,
