    }

    #tree_thread_list .current_posting > .forum_index-row
    {
        background-color: var(--color-black-translucent-50);
    }

    .heisetopnavi.v14
    {
        padding: 0.25em 0 !important;
        background-color: var(--color-darkest) !important;
    }

    div.post blockquote
    {
        border-left: 4px solid var(--color-dark) !important;
        color: var(--color-dark) !important;
    }

    #logo_bereich.socialmedia .navigation li a
    {
        color: var(--color-light) !important;
    }

    .metaline .buttonline li,
