    #mitte_news .meldung_wrapper li a,
    #mitte_news .meldung_wrapper p a
    {
        color: var(--color-accent) !important;
    }

    #themen_aktuell.linear
    {
        margin: 0 !important;
        padding: 0 1.5rem !important;
        height: 3rem !important;
        overflow: hidden !important;
        line-height: 3rem !important;
        border-bottom: none !important;
    }

    #themen_aktuell.linear,
    .breadcrumb
    {
        background-color: var(--color-darker) !important;
    }

    #mitte_news .nachricht a
    {
        color: var(--color-accent) !important;
    }

    .channel .stage-header .stage-slug
    {
        color: var(--color-lightest) !important;
    }

    .player .video_titel,
    .source,
    .source a,
    .bildunterschrift
    {
        color: var(--color-light) !important;
    }

    li.date
    {
        color: var(--color-lightest) !important;
        font-weight: normal !important;
    }

    .heise-table-title
    {
        background-color: var(--color-lightest) !important;
        color: var(--color-accent) !important;
        font-weight: 700 !important;
        text-align: center !important;
    }

    .a-inline-table__table tr:nth-child(2n) td,
    .a-inline-table__table tr:nth-child(2n) th
    {
        border-color: var(--color-darkest);
        background-color: var(--color-darkest);
    }

    .a-inline-table__table td,
    .a-inline-table__table th
    {
        border-color: var(--color-darkest);
    }

    #news_notify_hint
    {
        font-size: 11px !important;
        color: var(--color-light) !important;
        background-color: var(--color-darker) !important;
        border: 1px solid var(--color-light) !important;
    }

    .heisetopnavi.v14 nav
    {
        background-color: var(--color-darkest) !important;
    }

    #sitemap,
    #sitemap *
    {
        background-color: var(--color-darkest);
    }

    #ho_themenseite_letzte,
    #ho_themenseite_letzte *
    {
        background-color: var(--color-darkest);
    }

    #logo_bereich.socialmedia
    {
        background: var(--color-darker) !important;
    }

    #themen_aktuell.linear p,
    .tp_beitragsliste p,
    .teaser_vertrieb_events_highlights li,
    .teaser_vertrieb_events_untertitel,
    .teaser_buecher h3.h5
    {
        color: var(--color-light);
    }

    #themen_aktuell.linear ol li a
    {
        color: var(--color-accent) !important;
    }

    #logo_bereich.socialmedia .navigation li a:hover
    {
        color: var(--color-accent) !important;
    }

    .heisetopnavi.v14 #heisetopnavi_sub
    {
        background: transparent !important;
    }

    .heisetopnavi.v14 .heisetopnavi_button.active + #heisetopnavi_sub_container,
    .heisetopnavi.v14 .heisetopnavi_logo.active + #heisetopnavi_sub_container
