        -webkit-backdrop-filter: blur(8px) !important;
        backdrop-filter: blur(8px) !important;
        box-shadow: none !important;
        overflow: hidden;
    }

    .heisetopnavi.v14 a:link,
    .heisetopnavi.v14 a:visited .heisetopnavi a:link,
    .heisetopnavi a:visited
    {
        color: var(--color-accent) !important;
    }

    .heisetopnavi.v14 #heisetopnavi_sub a:focus,
    .heisetopnavi.v14 #heisetopnavi_sub a:hover
    {
        color: var(--color-lighter) !important;
        background-color: var(--color-light-translucent-50) !important;
        border: 1px solid !important;
        border-radius: 0.5em !important;
        font-weight: 700 !important;
    }

    #mitte #mitte_rechts
    {
        background: var(--color-darker) !important;
        width: 32% !important;
    }

    #mitte #mitte_links
    {
