style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.omegle.com") {

  #footer, #header, #mobilesitenote, #introtext,
  #monitoringnotice, #chattypeheaderrow, #topicsettingscell,
  .questionHeading {
    display: none;
  }
  
  html {
    max-height:1000px;
  }
  
  body {
    background: #1b1b1b;
    text-align:center;
    height:100%;
    max-height:1080px;
    position: relative;
  }
  
  #intro {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: #1b1b1b;
    border: solid 1px white;
    color: whitesmoke;
    font: 24pt sans;
    width:960px;
  }
  
  #topterms {
    color: inherit;
  }
  
  .logwrapper {
    background: #1c1c1c;
    border: solid 1px white;
    margin: 0 0 !important;
    top: 0 !important;
  }
  
  .controlwrapper {
    margin: 0 0 !important;
  }
  
  .question {
    left: 50%;
    transform: translate(+15%);
    background: #1b1b1b; 
  }
  
  .questionText {
    background: inherit;
    color:whitesmoke;
    border: solid 1px white;
  }
  
  .logitem, .msgsource {
    color: whitesmoke;
  }
  
  .youmsg .msgsource {
    color: lightblue !important;
  }
  
  .strangermsg .msgsource {
    color: pink !important;
  }
  
  .youmsg, .strangermsg {
    float: left;
    clear:both;
  }
  
  .statuslog {
    clear:both;
  }
  
  a {
    color: aliceblue !important;
  }
  
  
}
