style.css:
@-moz-document domain("factoryidle.com") {
.mainWithAdd > div:nth-child(1),
.mainWithAdd > div:nth-child(2),
.mainWithAdd > br,
.mainWithAdd > div:last-child {
	display: none;
}

.main, .mainWithAdd {
	position: relative;
	width: 100% !important;
	height: 100% !important;
}

#gameArea {
	position: absolute;
	width: 100%;
	height: 100%;
	margin-top: 0;
	border: 0;
}


.mapContainer {
	position: absolute;
}

.mapContainer,
.mapContainer > div {
	width: 100% !important;
	height: 100% !important;
}

#gameArea > table {
	position: absolute;
	padding: 1em;
	box-sizing: border-box;
	display: block;
	width: 100%; height: 100%;
}

tbody {
	display: flex;
	flex-direction: column;
	height: 100%;
}

.factoryBox tr:nth-child(2) {
	flex: 1;
}

tr {
	display: flex;
}

td {
	display: block;
}

td.overviewArea {
	min-width: 219px;
}

td.mapArea {
	flex: 1;
	position: relative;
}

td.topArea {
	flex: 1;
	min-width: 630px;
	max-width: 834px;
}
}
