style.css:
@-moz-document domain("fastmail.com") {

/* navbar */
.v-PageHeader {
    background: #1d1d1d;
}

/* the create rule button has its own blue gradient applied, instead */
/* of using generic button one. so re-apply with importance. */
.v-PageHeader-section > .v-Button,
.v-SearchText > .v-Button {
    background-image: linear-gradient(#fff3, #ffffff13) !important;
}

/* pin/unread/etc filters */
.v-PushSelect {
    display: none !important;
}

/* sidebar toggle button */
.v-Button--sidebar.is-highlighted::before {
    display: none;
}


/* left sidebar (folders) */
.s-new-message {
    margin: 0 !important;
    width: 100%;
    background: #2D3236 !important;
    color: #B2B6B9 !important;
}

.s-new-message > .label {
    padding-left: 8px;
}

.v-SearchInput-input:focus {
    border-color: #8ea4ff;
    box-shadow: none;
}

.v-Page-content {
    background: #111;
}

/* item highlight color */
.app-source.is-selected {
    color: #8ea4ff;
}

/* badge styles */
.v-MailboxSource-badge {
    border: none;
    padding: .1em .5em;
}

/* space quota */
.v-Quota {
    opacity: 0.3;
    border: none;
    background-color: transparent;
}

.v-Quota:hover {
    opacity: 0.8;
}

.v-Quota--91to100 {
    border: none;
}

.v-Quota--91to100 .v-Quota-storage {
    color: #fff;
}

.v-Sidebar-footer {
    background: #111;
}


/* inbox content */
/* ------------- */

/* keyboard-select indicator */
.v-ListKBFocus {
    background: #8ea4ff;
}

/* view options bar (sorting, pinning, etc) */
.v-Page--contrast .v-Page-viewOptions,
.v-Page-viewOptions {
    background: #111;
}

/* hide pinning */
#v-PinSelect {
    display: none;
}

/* an email in the inbox */
.v-MailboxItem {
    background: #111;
}

.v-MailboxItem.is-unread {
    background: #3a3a3a;
}

/* remove the "new message" dot */
.v-MailboxItem.is-unread .v-MailboxItem-from {
    padding-left: 0;
}
.v-MailboxItem-unreadbadge {
    display: none;
}

.v-MailboxItem.is-selected {
    background: #333a44;
}

/* hide the pin button */
.v-MailboxItem div:last-child {
    display: none;
}

/* extend the sender and preview to take up the space freed up by the pin button */
/* this is 25px left of where it was before */
.v-MailboxItem-from,
.v-MailboxItem-preview {
    left: 60px;
}
.v-MailboxItem-attachments {
    margin-left: 61px;
}
.v-MailboxItem-subject {
    margin-left: 84px;
}

/* move the reply icon to take up space freed by the removing the pin icon */
.v-MailboxItem-replied {
    left: 18%;
}
.v-Mailbox--vlong .v-MailboxItem-replied {
    left: 150px;
}

/* search highlighting */
mark {
    background-color: transparent;
    color: #8ea4ff;
}

/* the 1-5 of 100 note in the bottom right corner */
.v-StatusBar {
    opacity: 0.5;
}

.v-StatusBar:hover {
    opacity: 1;
}

/* compose view */
/* ------------ */
:not(.v-MenuOption) > .v-Button--cta {
    background: var(--ui-button-simple-color-bg);
    color: var(--ui-button-simple-color-fg);
}
:not(.v-MenuOption) > .v-Button--cta:hover {
    background: var(--ui-button-simple-color-bg-hover);
}

.v-Compose-header .v-Button--subtle {
    color: rgb(214, 216, 218);
}

.v-TextInput--standard.is-focused .v-TextInput-input {
    border-color: #777;
    box-shadow: none;
}

/* message view */
/* ------------ */

.v-MessageCard-actions .is-pinned,
.v-MessageCard-actions .is-unpinned,
.v-MessageCard-actions .is-read,
.v-MessageCard-actions .is-unread {
    display: none;
}

/* the "remote images blocked banner" */
.v-Message-banner--info {
    background: none;
}

/* sender names */
.u-color-0 {
    color: #e6d6e6;
}
.u-color-1 {
    color: #ccd9d9;
}
.u-color-2 {
    color: #cad4dd;
}
.u-color-3 {
    color: #e1d6d6;
}
.u-color-4 {
    color: #ddd4d4;
}
.u-color-5 {
    color: #cbccd9;
}
.u-color-6 {
    color: #d2cac6;
}
.u-color-7 {
    color: #d5beba;
}
.u-color-8 {
    color: #b5b7bf;
}
.u-color-9 {
    color: #cab6c7;
}
.u-color-10 {
    color: #c4d1c9;
}
.u-color-11 {
    color: #e1c6d5;
}
.u-color-12 {
    color: #ddc1bd;
}
.u-color-13 {
    color: #d2d1dc;
}
.u-color-14 {
    color: #d5c9cc;
}
.u-color-15 {
    color: #9eb7b2;
}

.v-Message-banner--info {
    border-color: transparent;
}

}
