/* @import url(https://fonts.googleapis.com/css?family=Source+Code+Pro); */
@font-face {font-family:'Source Code Pro';font-style:normal;font-weight:400;src:local('Source Code Pro'),local('SourceCodePro-Regular'),url(https://fonts.gstatic.com/s/sourcecodepro/v6/mrl8jkM18OlOQN8JLgasDy2Q8seG17bfDXYR_jUsrzg.woff2)format('woff2');unicode-range:U+0100-024F,U+1E00-1EFF,U+20A0-20AB,U+20AD-20CF,U+2C60-2C7F,U+A720-A7FF;}
@font-face {font-family:'Source Code Pro';font-style:normal;font-weight:400;src:local('Source Code Pro'),local('SourceCodePro-Regular'),url(https://fonts.gstatic.com/s/sourcecodepro/v6/mrl8jkM18OlOQN8JLgasD9V_2ngZ8dMf8fLgjYEouxg.woff2)format('woff2');unicode-range:U+0000-00FF,U+0131,U+0152-0153,U+02C6,U+02DA,U+02DC,U+2000-206F,U+2074,U+20AC,U+2212,U+2215,U+E0FF,U+EFFD,U+F000;}

/* Code Mirror: Window */
.cm-s-mozilla.CodeMirror {font-family:'Source Code Pro';background:#f8f8f8;color:#535353;border:none;outline:none;}
.cm-s-mozilla .CodeMirror-selected {background:#abdffa;}
.cm-s-mozilla .CodeMirror-activeline-background {background:none;}
.cm-s-mozilla .CodeMirror-matchingbracket {background:#cfead6;color:#232323!important;outline:none!important;}
.cm-s-mozilla .CodeMirror-gutters {background:#f1f1f1;border:none;}
.cm-s-mozilla .CodeMirror-linenumber {color:#949494;}

/* Code Mirror: Code */
.cm-s-mozilla .cm-keyword {color:#446fbd;}
.cm-s-mozilla .cm-atom {color:#e88501;}
.cm-s-mozilla .cm-number {color:#6d8600;}
.cm-s-mozilla .cm-def {color:#8757ad;}
.cm-s-mozilla .cm-variable, .cm-s-mozilla .cm-variable-2, .cm-s-mozilla .cm-variable-3 {color:#535353;}
.cm-s-mozilla .cm-property {color:#8757ad;}
.cm-s-mozilla .cm-comment {color:#949494;}
.cm-s-mozilla .cm-string, .cm-s-mozilla .cm-string-2 {color:#e88501;}
.cm-s-mozilla .cm-meta {color:#535353;}
.cm-s-mozilla .cm-error {color:#dc322f;}
.cm-s-mozilla .cm-qualifier {color:#446fbd;}
.cm-s-mozilla .cm-builtin {color:#446fbd;}
.cm-s-mozilla .cm-tag {color:#446fbd;}
}
