@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {

.subredditbox, .organic-listing, .sidebox.submit, .sidebox.create, .sitetable, .side, .footer, .nextprev, .hover, .raisedbox, #header-bottom-left, 
.popup, .infobar, #header-bottom-right .separator, img, li, span, body, .morelink, a, .dropdown.srdrop .selected.title, 
.dropdown.tabdrop, .selected.title, .organic-listing .entry, .organic-listing .odd.link, .organic-listing .even.link, .help tr, .details th, .score, 
.flat-list.hover .separator, #header-bottom-right .separator{ background-color:rgb(245,245,245)!important}

#sr-header-area .dropdown.srdrop .selected.title{ color:rgb(245,245,245)!important}

div.commentbody.border, .commentbody.border .md p a:link, .commentbody.border .md p a:visited, .commentbody.border .md ul li, input, 
.commentreply textarea, th, #sr-header-area, #sr-bar a, .separator, #sr-header-area .flat-list.hover .separator, .helpcover, .helpcover p, 
.helpcover .state-button span, .helpcover .state-button span a, #sr-header-area .dropdown.srdrop .selected.title{ background-color:#2C2C2C  !important}

.nextprev, .nextprev.a, a:active, .odd .comments a:link, .selected, .dropdown-title, .profline, .commentbody .md p a:link, a.friend:link, 
a.friend:visited, .head.new, h3, .rank a.star, .flat-list.buttons a, .score.likes, 
.details th{ color:#2C2C2C!important}

p, a:link, #cover_disclaim, td, .raisedbox, h1{ color:#2C2C2C!important}

.tagline .hover, .commentbody.border .md p a:link, .commentbody .md ul, .entry .flat-list.buttons .first .comments, .entry .author{ color:#2C2C2C!important}

.commentbody.border .md p a:visited, .commentbody .md p a:visited{ color:#2C2C2C!important}

div.commentbody.border p, input, .commentreply textarea, th, .score.dislikes, .helpcover, .helpcover p, 
.helpcover .state-button span, .helpcover .state-button span a{ color:white!important}

.selected.title, .flat-list.buttons span a, .flat-list.buttons .bylink, .flat-list.buttons .first a, .spacer .label, .score, 
.entry .flat-list.buttons li a, .entry .flat-list.buttons .watch-play, .entry .flat-list.buttons .watch-stop{ color:#2C2C2C!important}

#sr-header-area, #sr-bar a, .separator{ color:rgb(245,245,245)!important}

a:visited{ color:rgb(95,95,95) !important}

body{ color:#2C2C2C!important}

span.rank{ color:#2C2C2C!important}

.span.error{ color:red !important}

.comment .child{ border-left:1px dotted #2C2C2C !important}

.nextprev, .nextprev.a, .infobar, input, .dropdown-title, a.star{ font-weight:bold !important}

form h5, form h4, #adlink, #sr-more-link, .bottommenu, .wired, .infobar, #ad-frame, .thumbnail{ display:none !important}

.sidebox.submit, .sidebox.create{ background-image:none!important}

#search input{ position:fixed !important;  height:15px!important;  width:275px!important;  right:12px !important;  bottom:12px !important;  font-size:11px!important;  text-align:right!important}

#header-bottom-right{ position:fixed!important;  right:12px!important;  bottom:40px!important;  padding:0px!important}

#header-img{ width:0px !important;  height:40px !important;  padding-left:120px !important;  position:fixed!important;  right:10px!important;  bottom:60px!important}
 
#header-img{  background:url("data:image/png; base64,") !important}

#header-bottom-left{ height:40px !important}
 
.cap-text{ width:260px !important}

#header-bottom-left{ height:20px!important;  padding-top:5px!important}

.hover#header-top{ position:absolute !important;  top:15px !important;  right:12px !important}

#header, a, .subredditbox{ margin:0px !important;  border:0px !important;  padding:0px !important}

.infobar{ padding:10px !important}
 
span.rank{ padding:6px !important}

.sidebox.submit, .sidebox.create{ border:0px !important}

#sr-header-area, #sr-bar a, .separator{ border:none!important}

#sr-header-area .flat-list.hover .separator{ margin-right:0px!important;  margin-left:0px!important}

#header-bottom-right .separator{ margin:0px!important}

.footer{ padding-bottom:40px!important}

}
