style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soundcloud.com") {
  
/*Hue Change*/
    .sceneLayer, .sc-button-play, .playbackTimeline, .heroPlayButton {
    filter: hue-rotate(240deg);
  }
  
  .waveformCommentsNode {
    filter: hue-rotate(0deg) !important;
  }
  
/*Nav Bar*/  
  .header {
    background: #673ab7;
    /*box-shadow: 0 2px 5px rgba(0,0,0,0.26);*/
  }
  
  .header__logo {
        background: linear-gradient(rgba(255, 119, 0, 0),rgba(255, 51, 0, 0));
  }
  
  .g-dark a, .g-dark .sc-type-light {
    color: #FFF;
  }
  
  .header__navMenu > li > a {
    border-right: 1px solid #673AB7;
  }

  .headerSearch__input {
    height: 49px;
  }
  
  .sc-input {
    border-radius: 0px;
  }
  
  .g-dark textarea, .g-dark select, .g-dark input[type="text"], .g-dark input[type="password"], .g-dark input[type="search"] {
    border: 0;
  }
  
  .header__navMenu > li > a.selected, .header > li > a:focus {
  background-color: #4527a0;
  }
  
  input {
    margin-top: -12px;
    margin-right: -10px;
    margin-bottom: -10px;
    margin-left: -10px;
  }
  
  .headerSearch__submit {
    border: 0;
    padding: 20px;
    background: url(https://a-v2.sndcdn.com/assets/images/header/search-dbfe5c.svg) 0 0 no-repeat;
    width: 15px;
    height: 15px;
    position: absolute;
    right: 10px;
    top: 17px;
  }
  
  
/*Playbar*/
  .playControls__inner {
    background-color: #212121;
    border-top: 1px solid rgba(255, 255, 255, 0);
  }
  
  .playControl {
    background: url(https://a-v2.sndcdn.com/assets/images/playback/play-c9d2fb.svg) no-repeat 55% center;
    filter: invert(100%);
  }
  
  .skipControl__next {
    background-image: url(https://a-v2.sndcdn.com/assets/images/playback/skip-3aac31.svg);
    filter: invert(100%);
  }
  
  .skipControl__previous {
    background-image: url(https://a-v2.sndcdn.com/assets/images/playback/prev-34697d.svg);
    filter: invert(100%);
  }
  
  volume[data-level="10"] .volume__button {
    background-image: url(https://a-v2.sndcdn.com/assets/images/playback/volume-max-7a4115.svg);
    filter: invert(100%);
  }
  
  .volume__button {
    background: url(https://a-v2.sndcdn.com/assets/images/playback/volume-muted-624fac.svg) no-repeat left center;
    filter: invert(100%);
  }
  
  .sc-button-active, .sc-button-selected {
    color: #673ab7;
  }
  
  .volume__sliderHandle {
    background-color: #673ab7;
  }
  
  .volume__sliderProgress {
    background: #673ab7;
  }
  
  a.playbackSoundBadge__context:hover, a.playbackSoundBadge__context:focus {
    color: #FFF;
  }
  
  .playbackSoundBadge__title:hover, .playbackSoundBadge__title:focus {
    color: #FFF;
  }
  
  .playbackTimeline__duration {
    color: #CCC;
  }
  
  .userNav__item.selected, .userNav__button.selected {
    color: #FFF;
    background-color: #4527A0;
  }
  
  
/*Global*/
  .sc-button-small.sc-button-selected.sc-button-like::before, .sc-button-small.sc-button-selected.sc-button-like.sc-button-lightfg::before, .sc-button-small.sc-button-selected.sc-button-like.sc-button-visual::before {
    background-size: 16px 16px;
    background-image: url(data:image/svg+xml;base64,);
    filter: hue-rotate(240deg);
  }
  
  .g-tabs-link.active {
    border-color: #673ab7;
    color: #673ab7;
  }
  
  .sc-button-dropdown::after {
    content: ' ';
    border-right: 1px solid #673ab7;
    border-bottom: 1px solid #673ab7;
  }
  
  .sc-button-dropdown {
    cursor: pointer;
    color: #673ab7;
  }
  
  .sc-button-cta, .sc-button-cta:hover, .sc-button-cta:focus, .sc-button-cta:visited {
    background-color: #673ab7;
    border-color: #673ab7;
  }
  
  .sc-status-icon {
    display: inline-block;
    vertical-align: top;
    overflow: hidden;
    width: 12px;
    height: 12px;
    text-indent: 20px;
    background-size: 12px 12px;
    background-image: url(data:image/svg+xml;base64,);
    filter: hue-rotate(240deg);
  }
  
  .g-dark .sc-button-selected:hover:not(.sc-button-cta), .g-dark .sc-button-selected:focus:not(.sc-button-cta) {
    border-color: #673ab7;
  }
  
  .g-dark .sc-button-selected:not(.sc-button-cta) {
    color: #673ab7;
  }
  
  .sc-button-small.sc-button-selected.sc-button-repost::before, .sc-button-small.sc-button-selected.sc-button-repost.sc-button-lightfg::before, .sc-button-small.sc-button-selected.sc-button-repost.sc-button-visual::before {
    background-size: 16px 16px;
    background-image: url(data:image/svg+xml;base64,);
    filter: hue-rotate(240deg);
  }
  
  .sc-icon-medium.sc-icon-shuffle-orange {
    background-image: url(data:image/png;base64,);
    filter: hue-rotate(240deg);
    background-size: 19px 15px;
  }
  
  .sc-toggle::before {
    content: '';
    position: absolute;
    left: -2px;
    top: -2px;
    background: #673ab7;
    transition: opacity .2s;
    opacity: 0;
  }
  
  element {
    background-color: rgb(103, 58, 183);
  }
  
  .progressBar__bar {
    background: #673ab7;
  }
  
  .sc-button-active, .sc-button-selected {
    color: #673ab7;
  }
  
  .sc-label.sc-label-private {
    background: #673ab7;
  }
  
  .sc-orange {
    color: #673ab7;
  }
  
  .sc-button-active, .sc-button-selected {
    color: #673ab7;
  }

  
  .searchOptions__navigationItem.active {
    position: relative;
    background: #673ab7;
  }

  .g-nav-item.active > .g-nav-link {
    background-position: 5px -28px;
    color: #fff;
    text-shadow: 0 1px 0 #251442;
  }
  
  .searchOptions__navigationItem.active::after {
    left: 100%;
    top: 50%;
    border: solid transparent;
    content: " ";
    height: 0;
    width: 0;
    position: absolute;
    pointer-events: none;
    border-left-color: #673ab7;
    border-width: 15px;
    margin-top: -15px;
  }

  .sc-button-dropdown {
    border: 1px solid #673ab7;
    cursor: pointer;
    color: #673ab7;
    text-align: left;
    text-overflow: ellipsis;
  }
  
  .commentPopover__username, a.commentPopover__username:hover, a.commentPopover__username:visited {
    color: #673ab7;
  }
  
  .commentPopover__username::before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    width: 1px;
    display: block;
    background-image: linear-gradient(rgb(103, 58, 183),rgba(255,85,0,.1));
  }
  
  .uploadButton__status {
    display: none;
    margin-left: .3em;
    color: #aa9dc2;
  }
  
  .progressBar__inner {
    background-color: #673ab7;
    background-image: linear-gradient(to right,#673ab7,#673ab7);
    -webkit-transform-origin: 0 0;
    transform-origin: 0 0;
    -webkit-transform: scaleX(0);
    transform: scaleX(0);
  }
  
  .g-required-label::after {
    content: '*';
    color: #673ab7;
    display: inline-block;
    margin-left: 2px;
  }
  
  .blockCheckbox__icon {
    background: url(https://a-v2.sndcdn.com/assets/images/upload/big-checkbox-583f9f.png) no-repeat 0 0;
    filter: hue-rotate(240deg);
    background-size: 26px 156px;
    width: 26px;
    height: 26px;
    margin: 2px 0 0 2px;
    border-radius: 50%;
  }
  
  .sc-button-active, .sc-button-selected {
    color: #673ab7;
  }
  
  .compactTrackListItem.active, .compactTrackListItem.active .compactTrackListItem__number, .compactTrackListItem.active .compactTrackListItem__content, .compactTrackListItem.active .compactTrackListItem__trackTitle, .compactTrackListItem.active .compactTrackListItem__user, .compactTrackListItem.active .compactTrackListItem__plays {
    color: #673ab7;
  }
  
  .trackItem.active:not(.m-disabled) .trackItem__trackTitle, .trackItem.active:not(.m-disabled) .trackItem__content {
    color: #673ab7;
  }
  
  .sc-button-selected.sc-button-medium.sc-button-like::before {
    background-size: 16px 16px;
    background-image: url(data:image/svg+xml;base64,);
    filter: hue-rotate(240deg);
  }

}
