style.css:
/* PeerAnswer Big Style by geerky42 */

@-moz-document url-prefix('https://www.peeranswer.com/') {
	/* Increase all fonts by average of approximately 20 percent*/
	
	* div, textarea, input, .topbarButton {
		font-size: 16px !important;
	}
	#OnlineTitleNumber, #OnlineTitleSubject {
		font-size: 31px !important;
	}
	.post_content, .sub span, #time_spent {
		font-size: 22px !important;
	}
	.main, #profileName {
		font-size: 38px !important;
	}
	#profileLevel_num, #profileLike_num, #profileView_num {
		font-size: 34px !important;
	}
	.room_invite {
		font-size: 23px !important;
	}
	/* Fix any display errors */
	
	.bChatMessage .time {
		right: 75px;
	}
	#change_subject_top {
		margin-left: 14px;
	}
	#subject_list_dropdown a:nth-child(5) span {
		width: 200px !important;
	}
	#subject_list_dropdown a:nth-child(12) span {
		width: 200px;
	}
	.OnlineUser, .OnlineRight {
		height: 90px !important;
	}
	.UserStat {
		max-height: 44px;
	}
	.QuestionsUsersLevel {
		width: 24px !important;
		height: 24px !important;
		line-height: 24px !important;
	}
}
