style.css:
@-moz-document url-prefix("http://tumblr.com"), url-prefix("https://tumblr.com"), url-prefix("https://www.tumblr.com"), url-prefix("http://www.tumblr.com") {
/*------------------------------------------------------------------

"I'm Tired of Tumblr Expanding the Sidebar" Anti-Sidebar Code
by Muffu
> Use this code in any theme you'd like its the internet its free game


------------------------------------------------------------------*/

.l-container.l-container--two-column-dashboard .right_column { 
    display: none !important; 
}

.l-container--two-column-dashboard .left_column  {
    float: none !important;
    width: 550px !important;
    margin: 0 auto !important;
}

.l-container.l-container--two-column-dashboard .left_column .posts { 
    width: 550px !important;
    margin: 0 auto !important; 
} 

}
