style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("3g.renren.com") {
  img[src*="large"], img[src*="original"] {
    width: 100%;
  }
}

@-moz-document domain("deviantart.com") {
  .mobile_instream_ad[style] {
    display: none !important;
  }
  .ad-container[style] {
    display: none !important;
  }
  .minibrowse-container {
    padding-top: 0 !important;
  }
  .ads-container {
    display: none !important;
  }
  .dp-ad-visible {
    display: none !important;
  }
  .fade-highlight-bar {
    display: none !important;
  }
}

@-moz-document domain("m.ign.com") {
  #knight {
    display: none !important;
  }
}

@-moz-document domain("www.52rkl.cn") {
  .footer + div {
    display: none !important;
  }
}

@-moz-document domain("mt.renren.com") {
  .dl_wrapper {
    display: none !important;
  }
  footer {
    margin-bottom: 0px !important;
  }
  .dl_btn{
    display: none !important;
  }
  .cansle_btn {
    background: #227dc5 !important;
  }
  .cansle_btn:before {
    content: "好的";
    display: block;
  }
}
