style.css:
@-moz-document domain("playstarfleet.com"), domain("playstarfleetextreme.com") {
.additional_content {
    display: none !important;
}
.commander {
    display: none !important;
}
.terraform_content {
    display: none !important;
}
.credits {
    display: none !important;
}
.merchant {
    width: 21px !important;
}
.resources {
    height: 105px;
    margin-top: -25px;
    position: absolute;
    top: 180px;
    right: 10px;
    width: 190px !important;
    z-index: 5;
    left: 580px !important;
}
.resources.starfleet {
    background-image: url('http://i.imgur.com/m3gcSZB.png') !important;
}
.spacer {
    height: 25px !important;
}
body {
    background: #000000 url('http://i.imgur.com/5fh6a3F.jpg') top left repeat-y !important;
    color: #FFFFFF;
    font-family: Arial, sans-serif;
    font-size: 12px;
    letter-spacing: 1px;
    margin: 0px;
}
.resources.extreme {
    background-image: url('http://i.imgur.com/m3gcSZB.png') !important;
}
.item_table {
    background-color: rgba(0, 0, 0, 0.4) !important;
}
tbody {
    background-color: rgba(0, 0, 0, 0.4) !important;
}
}
