style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("https://www.youtube.com/") {

/* Menu Haut */
#yt-masthead-container {background: #000;}
#yt-masthead-user #sb-button-notify {background: #666; border: 1px solid #cc181e; margin: 0 10px 0px 0px;}
#masthead-search-terms input {background: #333; color: #fff;}
.masthead-search-terms-border {border: 1px solid #cc181e; }
.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {border-color: #cc181e; background: #999; color:  #000;}
.gssb_m {color: #fff;}
.gssb_a, .gssb_a td {background-color: #333;}
/* Menu Haut */

/* ########################################################################################################################################## */

/* Menu Gauche */
#appbar-guide-menu, .guide-flyout {background: #333;}
.guide-pinned .guide-pinning-enabled #appbar-guide-menu {border-right: 1px solid #cc181e;}
.guide-item {color: #fff;}
.guide-section-separator {color: #cc181e;}
.signup-promo {color: #fff;}
/* Menu Gauche */

/* Le meilleur de youtube */
li.guide-section h3 {color: #cc181e;}
/* Le meilleur de youtube */

/* Menu Hamburger haut gauche */
.yt-uix-button-text, .yt-uix-button-text[disabled], #appbar-guide-button {border: solid 1px #cc181e; color: #cc181e;}
/* Menu Hamburger haut gauche */

/* bouton se connecter en bas à gauche */
#content-container, #body-container {background-color: #666;} /* Background encadrement div vidéo */
canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form {color: #cc181e;}
/* bouton se connecter en bas à gauche */
    
/* ########################################################################################################################################## */
/* ########################################################################################################################################## */
    
/* Page Principale */
#content-container, #body-container {background-color: #000;}
canvas, caption, center, cite, code, dd, del, dfn, div, dl, dt, em, embed, fieldset, font, form {background: #000;}
#appbar-guide-menu, .guide-flyout {background: #000;}
#yt-masthead-container {background: #000; border-bottom: 1px solid #cc181e;}
.branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {border-bottom: 1px solid #cc181e;}
.yt-default h3, h3.yt {color: #fff;} /* Choix langue */
#yt-picker-country-footer, #yt-picker-language-footer, #yt-picker-safetymode-footer {color: #666; background: #000;}
.yt-picker-hr {border-top: 1px solid #cc181e;}
.yt-picker-header {border-bottom: 1px solid #cc181e;}
.yt-picker-region-name {color: #999;}
#safety-form p.safety-submit {border-top: 1px solid #cc181e;}
.branded-page-v2-subnav-container {border-bottom: 1px solid #cc181e;}
.feed-header {border-bottom: 1px solid #cc181e;}
#browse-items-primary .item-section > li > .yt-lockup-tile, .branded-page-v2-primary-col .branded-page-box, .section-list li .item-section .branded-page-box {border-bottom: 1px solid #cc181e;}
.branded-page-v2-primary-col .branded-page-box.video-player-view-component:last-child, .branded-page-v2-primary-col .branded-page-box.c4-featured-content:last-child, .branded-page-v2-primary-col .branded-page-box.c4-featured-content-editor:last-child, .branded-page-v2-primary-col .branded-page-box.welcome:last-child {border-bottom: 1px solid #cc181e;}
.yt-uix-expander-ellipsis {background-color: transparent;}
.video-player-view-component .video-detail a {color: #cc181e;}
.yt-ui-ellipsis {background-color: transparent;}
.comment-renderer-text-content {color: #999;}

/* Commentaire */
.comment-simplebox-frame {border: 1px solid #cc181e;}
.comment-simplebox {background-color: #000;}
.comment-simplebox-text, .comment-simplebox-prompt {background-color: #000; color: #999;}
.comment-simplebox-renderer {border-bottom: 1px solid #cc181e;}
.comment-simplebox-renderer-collapsed-content {border: 1px solid #cc181e; border-top: 1px solid #cc181e; color: #999;}
.comment-renderer-header .comment-author-text {color: #cc181e;}
.comment-renderer-like-count {color: #cc181e;}
/* Commentaire */
    
#action-panel-details a {color: #cc181e;}
#watch-description {color: #999;}

/* A propos */
.about-metadata-container {color: #999;}
.branded-page-related-channels h3 a, .branded-page-related-channels h3 {color: #999;}
.video-player-view-component .description {color: #999;}

/* Menu accueil / Tendance caché et remonté */
#appbar-nav, #appbar-content, #masthead-appbar, #appbar-nav-content-hidable, #masthead-appbar-container {display: none;}
#masthead-positioner-height-offset {height: 60px;}
/* Menu accueil / Tendance caché et remonté */

/* Nombre de vue et année d'upload */
.yt-lockup .yt-lockup-meta a, .yt-lockup .yt-lockup-description a, .yt-lockup .yt-lockup-byline a {color: #fff;}
.yt-lockup-title a, .yt-lockup:hover, .yt-lockup:hover {color: #cc181e; background-color: transparent;}
.yt-lockup-meta-info {color: #999;}  
/* Nombre de vue et année d'upload */
    
/* Page Principale */

/* ########################################################################################################################################## */
/* ########################################################################################################################################## */

a {color: #cc181e;}
.yt-lockup-title a, .yt-lockup:hover a, .yt-lockup:hover .yt-lockup-meta a, .yt-lockup:hover .yt-lockup-description a {color: #fff;}
.yt-uix-button-primary, .yt-uix-button-primary[disabled], .yt-uix-button-primary[disabled]:hover, .yt-uix-button-primary[disabled]:active, .yt-uix-button-primary[disabled]:focus {border-color: #cc181e; background: #cc181e;     color: #fff;}#watch8-action-buttons {border-top: 1px solid #cc181e;}
.video-extras-sparkbar-likes {background: #cc181e;}
#watch8-action-buttons .yt-uix-button, #watch8-action-buttons .yt-uix-button:hover, #action-panel-dismiss, #action-panel-dismiss:hover {background: #999;}
.yt-card .yt-uix-button-expander {color: #999; border-top: 1px solid #cc181e;}
.comment-replies-renderer-paginator, .comment-replies-renderer-view, .comment-replies-renderer-hide {color: #cc181e;}    
.yt-uix-checkbox-on-off input[type="checkbox"]:checked + label {background-color: #cc181e;}
.video-list .video-list-item .title {color: #cc181e;}
.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {border-color: #cc181e; background: #666; color: #000;}

/* AIDE */
.ghp-header {background-color: #cc181e;}
.ghp-iconTextComponent-label {color: #999;}
.ghp-button-label {color: #cc181e;}

/* ########################################################################################################################################## */

/* barre footer */
.yt-card {background: #000;}
body #footer-container {background-color: #000; border-top: 1px solid #cc181e;}
#footer-container {background-color: #fff; border-top: 1px solid #cc181e;}
#footer-main {border-bottom: 1px solid #cc181e;}
.yt-uix-button-link {background: transparent; color: #999;}
  
}
