style.css:
@-moz-document domain("osusearch.com") {
#root {
    background-color: black !important;
}
.column {
    background-color: black !important;
}
.title.difficulty {
    background-color: black !important;
    color: #80827a !important;
}
:-ms-input-placeholder {
    color: #80827a !important;
}
.ui.basic.button.select-button,
.ui.input input {
    background-color: #663300 !important;
    color: #80827a !important;
}
div.noUi-handle.noUi-handle-lower,
div.noUi-handle.noUi-handle-upper {
    background-color: black !important;
    color: #80827a !important;
    box-shadow:none;
    border: 1px solid #ffffff !important;
}
div.noUi-origin.noUi-connect {
    background-color: #663300 !important;
}
div.row,
div.column.beatmap.ranked,
div.column.beatmap.unranked,
div.column.beatmap.qualified,
div.ui.grid,
div.ui.selection.compact.dropdown,
div.item.active.selected,
div.item {
    background-color: black !important;
    color: #80827a !important;
}
div.row.field-row,
div.ui.column.input,
h2,
div.ui.menu.navbar.inverted.pink,
.ui.compact.selection.dropdown,
.ui.grid>.row,
.column.beatmap > .ui.grid {
    background-color: black !important;
    color: #80827a !important;
}
div.title.misc-title.difficulty {
    font-size: 15px !important;
    padding-bottom: 10px !important;
}
h2 {
    border-color: #663300 !important;
    border-width: 0px 0px 2px 0px !important;
    color: #80827a !important;
}
label {
    background-color: black !important;
    color: #80827a !important;
    padding: 10px !important;
}
button.large.fluid.pink.ui.button,
button.large.fluid.purple.ui.button,
button.pink.ui.button {
    background-color: #663300 !important;
    color: #ff9900 !important;
}
a {
    color: #ff9900 !important;
}
a.ui.white.label,
a.ui.tag.label.priority-2{
    color: #000000 !important;
}
::-moz-placeholder {
    color: #ff9900 !important;
}
::-webkit-input-placeholder {
    color: #ff9900 !important;
}
}
