style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("laracasts.com") {
  body,
  .Box 
  { 
    background-color: #3c3e42; 
  }  
  .wrap,
  .Box 
  { 
    padding: 0; 
  }  
  .container,
  .Video__player 
  { 
    width: 100%; 
  }  
  .Video__player 
  { 
    margin-top: 0; 
  }  
  .Box 
  { 
    border: none; 
  }  
  header.Header,
  footer.Footer,
  .Footer-Secondary,
  .Video__information,
  .wrap > section.wrap,
  #root > .wrap 
  { 
    display: none; 
  }
}
