style.css:
/* i really want this to be global */

.asanaView-paneGutter,
.asanaView-body,
div.Topbar {
    background: #1b2432;
    border-bottom: 1px solid #2e3c54;
}
div.BoardColumnHeader {
    color: #fff;
}
/* Active menu highlight */

.NavigationLink.is-selected {
    color: #1aafd0;
}
/* Project info bar */

.full-width-header.full-width-header--compact .center-pane-header-row-view,
.full-width-header.full-width-header--compact,
.PageHeaderCollapsedStructure {
    background: #262f3e;
}
div#project_pane_header_name,
li.tab-nav > a.tab-nav-button.is-selected,
div.header-name.read-only,
div.PageHeaderCollapsedStructure-title,
div.Tab--isSelected {
    color: #fff !important;
}
/* Disable unneded upgrade and help buttons */

a.CircularButton.CircularButton--enabled.CircularButton--small.topbarHelpMenuButton.topbarHelpMenuButton-circularButton,
a.Button.Button--small.Button--primary.topbarContingentUpgradeButton-button {
    display: none;
}
.react-mount-node .omnibutton-button {
    background: none;
}
/* Central task pane */

div#center_pane__contents,
.grid-uncompact #grid .task-row:not(.recently-changed) .grid-tags-and-date,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:not(.recently-changed) .grid-tags-and-date,
.grid-uncompact #grid .task-row:not(.recently-changed) .grid-custom-fields,
.grid-uncompact #grid .grid_pseudorow_no_prioritized:not(.recently-changed) .grid-custom-fields {
    background: #f8f8f9;
}
