style.css:
@-moz-document domain("getbootstrap.com") {
/**
 * Bootstrap Sublime Theme (Jun 28, 2016)
 * License: https://creativecommons.org/licenses/by-sa/4.0/
 */

 /* Background (heavy metal) */
 body, .navbar, .bs-docs-featurette, .bs-glyphicons-list li,
 .bd-featurette {
 	background: #272822;
 }

 /* Navbar (prelude) */
 .nav-item.nav-link {
 	color: #cdbfe3 !important;
 }

 /* All (spring wood) */
 p, h1, h2, h3, h4, li, th, strong, div,
 [class*="col-"] > h3, .bd-featurette-title,
 #import-drop-target > p.lead, .post-title > a {
 	color: #F8F8F2;
 }
 /* [forced] */
 .navbar a, .bs-docs-featurette-title,
 #top > div > div > a, .post-title,
 .nav-item.nav-link.active,
 .bd-toc-link, .active.bd-sidenav-active > a,
 .post p {
 	color: #F8F8F2 !important;
 }
 /* (heavy metal) */
 .bs-example * {
 	color: #272822;
 }
 /* [forced] */
 .navbar a:hover,
 #bs-navbar > ul > li.active > a {
 	color: #272822 !important;
 }
 /* (bombay) */
 .text-muted, h4 {
 	color: #aeb4b7 !important;
 }

 /* Links (spray) */
 a {
 	color: #66D9EF;
 }
 /* (java) */
 a:hover {
 	color: #16b1d0;
 }
 
/* Buttons */
.btn-outline {
	border-color: #c9bbdd;
	color: #c9bbdd;
}

 /* Footer */
 .bd-footer.text-muted {
 	background: #563d7c;
 	color:#F8F8F2;
 }
 .bd-footer a {
 	color: #cdbfe3;
 }
}
