  }
  .selectpicker,
  .codebox code{
    margin: 0;
    border-color: #161616;
    background: #161616;
  }
  #content-forum{
    border-radius: 4px;
    background: #222;
    box-shadow: 1px 1px 3px #000;
  }
  section{
    border-radius: 4px;
    background: #2e2e2e;
    box-shadow: 1px 1px 3px #111;
  }
  .page-header{
    margin: -11px 0 0;
    padding: 10px 20px 0;
  }
  h3{
    color: #9e9e9e;
