    border-radius: 0 0 4px 4px;
    background: #2e2e2e;
    box-shadow: 1px 1px 3px #111;
  }
  .pagination ul > li > a,
  .pagination ul > li > a:hover{
    background: none;
  }
  .pagination ul > li > a:hover{
    color: #eee !important;
  }
  .btn, .pager li > a, .pager li > span{
    border-color: rgba(0,0,0,.1) rgba(0,0,0,.1) rgba(0,0,0,.25);
    background-color: #005296;
    background-image: linear-gradient(to bottom,#0062b6,#004286);
    background-repeat:no-repeat;
    box-shadow: inset 0 1px 0 rgba(255,255,255,.2),0 1px 2px rgba(0,0,0,.05);
    color: #bbb;
    text-shadow: 0 1px 1px rgba(0,0,0,.75);
  }
  .btn:hover, .btn.active, .btn.disabled, .btn:active, .btn:focus, .btn[disabled], .pager li > a:hover, .pager li > span:hover{
    background-color: #004286;
  }
  .pager li > a:hover, .pager li > span:hover, .pager li > a:focus, .pager li > span:focus{
    background-position: 0 -15px;
    transition: background-position .1s linear;
  }
  .btn-group.open .btn.dropdown-toggle{
    background-color: #004286;
  }
  .btn:focus, .btn:hover, .pager li > a:hover, .pager li > span:hover, .pager li > a:focus, .pager li > span:focus{
    color: #eee;
  }
  .pager li > a, .pager li > span{
    border: 0;
  }
  .pager li > a:focus, .pager li > a:hover{
    background-color: #004286;
  }
  .dropdown-menu{
    background-color: #005296;
    background-image: linear-gradient(to bottom,#0062b6,#004286);
  }
  .dropdown-menu li{
    border: none;
    background: none;
  }
  .dropdown-menu li:hover{
    background: none;
  }
  .dropdown-menu > li > a{
    color: #bbb;
    text-shadow: 1px 1px 1px #111;
  }
  .dropdown-menu > li > a:focus, .dropdown-menu > li > a:hover, .dropdown-submenu:focus > a, .dropdown-submenu:hover > a{
    color: #eee;
  }
  .ver-inline-menu li a, .ver-inline-menu li i{
    border: 0;
    background: #2e2e2e;
  }
  .ver-inline-menu li.active a, .ver-inline-menu li.active i{
    background: #0062b6;
  }
  .ver-inline-menu li.active::after{
    border-left: 6px solid #0062b6;
  }
  .ver-inline-menu li.active:hover::after{
    border-left: 6px solid #004286;
  }
  .ver-inline-menu li:hover a, .ver-inline-menu li:hover i{
    background: #004286;
  }
  .well-qr{
    background-color: #2e2e2e;
    box-shadow: 1px 1px 3px #111;
  }
 .table-white, .last-color{
    background: none !important;
    transition: all 0.25s ease;
  }
  .stats-color{
    background: #2e2e2e !important;
    transition: all 0.25s ease;
  }
  .table-hover tbody tr:hover > td, .table-hover tbody tr:hover > th {
    background-color: #333 !important;
  }
  .pagination span a, li.pagination span a{
    background-color: #004286;
  }
  .pagination span a:hover, li.pagination span a:hover{
    background-color: #0062b6;
  }
  p.separator i{
    background: #222;
  }
  blockquote{
    border-left: 5px solid #004286 !important;
    background: #2e2e2e !important;
    box-shadow: 1px 1px 3px #111 !important;
    padding: 8px 10px; 
  }
  .codebox{
    padding: 8px;
    border:0;
    border-radius: 4px;
    background: #2e2e2e;
    box-shadow: 1px 1px 3px #111;
  }
  .codebox span{
    border:0;
  }
  .page-category-topic h3{
    padding-bottom: 5px;
    border-bottom-color: #004286;
  }
  .page-category h3{
    color: #999;
    border-bottom-color: #004286;
  }
  .avatar-frame img{
    border: 0;
    border-radius: 3px;
    box-shadow: 1px 1px 4px #000;
  }
  .topstyle i{
    border: 0;
    border-radius: 22.5px;
  }
  .ribbon-wrapper .ribbon.on-line{
    background-color: #004286;
  }
  .table-striped tbody tr:nth-child(2n+1) td, .table-striped tbody tr:nth-child(2n+1) th{
    background-color: #161616;
  }
  .table-striped tbody tr td, .table-striped tbody tr th{
    transition: all 0.25s ease;
  }
  .table-primary,
  .table-primary.table-bordered tbody td{
    border: 0;
    box-shadow: none;
  }
  .table-primary thead th{
    border-color: #037;
    background-color: #004286;
  }
  .widget-body{
    border: 0;
    border-left: 1px solid #222;
    background-color: #2e2e2e;
  }
  ul.nav-tabs li a{
    border-top: 1px solid #222;
    border-right: 1px solid #222;
    border-left: 1px solid #222;
    background: #2e2e2e;
  }
  .nav-tabs > .active > a, .nav-tabs > .active > a:focus, .nav-tabs > .active > a:hover{
    border-top: 1px solid #222;
    border-right: 1px solid #222;
    border-bottom: 1px solid #2e2e2e;
    border-left: 1px solid #222;
    background-image: linear-gradient(to bottom,#222,#2e2e2e);
  }
  .tabbable-custom .nav-tabs > li.active{
    border-top: 3px solid #0062b6;
  }
  .nav > li > a:focus, .nav > li > a:hover{
    border-bottom: 1px solid #2e2e2e;
    background-color: #2e2e2e;
    background-image: linear-gradient(to bottom,#222,#2e2e2e);
  }
  .framed-ucp{
    border-color: #004286;
    background-color: #2e2e2e;
  }
  form .form-actions{
    border: 0;
    background: none;
    box-shadow: none;
  }
  #username_list{
    margin: 0;
  }
  .alert-info{
    border: 0;
    background-color: #333;
    box-shadow: 1px 1px 3px #111 !important;
    color: #999;
    text-shadow: 1px 1px 3px #111;
  }
  .close{
    color: #0062b6;
    text-shadow: 1px 1px 3px #111;
    opacity: 1;
    transition: all 0.25s ease;
  }
  .close:focus, .close:hover{
    color: #004286;
    text-shadow: 0px 0px 1px #111;
    opacity: 1;
  }
  .recent-activity li .icon{
    box-shadow: 0 0 4px 0 #333 inset, 0 0 4px 0 #111;
  }
  
  .footable.breakpoint > tbody > tr > td > span.footable-toggle{
    padding: 10px 13px;
    border-radius: 20px;
    background: #2e2e2e;
    box-shadow: 1px 1px 3px #111;
  }
  .footable.breakpoint > tbody > tr.footable-row-detail{
    background: #2e2e2e;
  }
  .footable.table.table-white.table-primary.table-hover.footable-loaded.phone.breakpoint .mobile-post{
    padding: 10px 11px;
    border-radius: 20px;
    background: #004286;
    box-shadow: 1px 1px 3px #111;
  }
  .progress{
    background-color: #222;
    background-image: linear-gradient(to bottom, #444, #333);
    box-shadow: 1px 1px 2px #111;
  }
  div.tagsinput{
    border: 0;
    background: none;
  }
  div.tagsinput span.tag{
    border: 0;
    background: #444;
    box-shadow: 1px 1px 2px #111;
  }
  .row-fluid .span4{
    width: 150px;
  }
  .row-fluid .span8{
    width: 100%;
  }
  @media(min-width: 768px){
    .row-fluid .span8{
      width: calc(100% - 180px);
    }
  }
  #message{
    width: 100% !important;
  }
  .collapse.in{
    overflow: visible;
  }
  form .control-group{
    border-color: #555;
  }
  .pmboots{
    border-color: #004286;
    background: none repeat scroll 0 0 rgba(16, 16, 16, 0.9);
    text-shadow: 1px 1px 1px #111;
  }
  td strong.pagination > span a, td strong.pagination > span .page-dots{
    border-left-color: #004286 !important;
  }
  .widget-header{
    border-color: #004286;
  }
  .widget-header a{
    color: #0062b6;
  }
/**/
/*Classic*/
/*[[Classic]]*/
  body{
    background: #111;
    color: #999;
  }
  .postdetails,
  .nav{
    color: #999;
  }
  .postauthor{
    color: #eee;
  }
  a:link{
    color: #aaa;
    text-decoration: none;
    transition: all 0.25s ease;
  }
  a:visited{
    color: #5bb;
  }
  a:hover{
    color: #5bf !important;
    text-shadow: 1px 1px 1px #000;
  }
  .spacer{
    background-color: #222 !important;
  }
  .row1,
  .row2,
  .row3{
    background: #222;
    transition: all 0.25s ease;
  }
  #menubar > table:nth-child(6),
  td.menu[align="left"],
  td.menu[align="right"],
  hr{
    display: none;
  }
  .tablebg{
    background: none;
    border-spacing:0;
    box-shadow: 1px 1px 3px #000;
  }
  .tablebg tr:hover .row1{
    background-color: #262626;
  }
  .tablebg tr:hover .row2{
    background-color: #262626;
  }
  .tablebg tr:hover .row3{
    background-color: #262626;
  }
  .tablebg[style="margin-top: 5px;"]{
    box-shadow: none;
  }
  .tablebg[style="margin-top: 5px;"] td{
    background: none !important;
  }
  .tablebg[style="margin-top: 5px;"] td:hover .row1{
    background-color: transparent !important;
  }
  p.breadcrumbs{
    padding: 5px 10px;
    background: #2e2e2e;
    color: #0062e6;
    box-shadow: 1px 1px 3px #000;
    border-radius: 0 10px 10px 0;
    transition: all 0.25s ease;
  }
  p.breadcrumbs:hover{
    background: #444;
    color: #0082f6;
    box-shadow: 1px 1px 1px #000;
  }
  p.breadcrumbs a{
    color: #ccc;
    text-decoration: none;
    transition: all 0.25s ease;
  }
  p.breadcrumbs a:hover{
    color: #5bf;
  }
  #menu_row{
    background: none;
  }
  #logo_row{
    position: absolute;
    width: 97.5%;
    top:0;
    z-index: -20;
    opacity: 0.2;
  }
  .postbody{
    padding: 5px 15px;
  }
  td.profile{
    padding: 6px;
  }
  th{
    background: linear-gradient(to bottom,#325375,#222);
  }
  .cat{
    background: #222;
    padding: 5px 0;
  }
  select{
    background-color: #111;
    color: #999;
    border-color: #111;
    border-radius: 3px;
  }
  .post,
  textarea{
    background: #0e0e0e;
    color: #bbb;
    border: 0;
    border-radius: 3px;
    box-shadow: inset 0 0 3px #000;
    padding: 3px;
  }
  #search{
    background: #2e2e2e;
    width: 325px;
    padding: 6px 15px;
    box-shadow: 1px 1px 3px #000;
    border-radius: 20px;
  }
  form[name="search"]{
    background: #2e2e2e;
    width: 265px;
    padding: 6px 15px;
    box-shadow: 1px 1px 3px #000;
    border-radius: 20px;
  }
  .quotecontent, .codecontent, .attachcontent{
    border: 0;
    background: #2e2e2e;
    color: #999;
  }
  .quotetitle, .codetitle, .attachtitle{
    border: 0;
    background: #2e2e2e;
    color: #ddd;
  }
  .quotewrapper, .codewrapper, .attachwrapper{
    border: 0 !important;
    background: #325375;
    box-shadow: 1px 1px 3px #000;
  }
  .btnbbcode,.btnmain,.btnlite{
    color: #bbb;
    text-shadow: 1px 1px 1px #333;
    font-size: 10px;
    font-weight: bold;
    border-color: rgba(0,0,0,.1) rgba(0,0,0,.1) rgba(0,0,0,.25);
    background-color: #246;
    background-image: linear-gradient(to bottom,#325375,#246);
    background-repeat:no-repeat;
    box-shadow: inset 0 1px 0 rgba(255,255,255,.2),0 1px 2px rgba(0,0,0,.05);
    text-shadow: 0 1px 1px rgba(0,0,0,.75);
    border-radius: 3px;
    transition: background-position .1s linear;
  }
  .btnbbcode:hover, .btnmain:hover, .btnlite:hover{
    color: #ddd;
    background-position: 0 -10px;
  }
  a[href="#wrapheader"]{
    font-size: 0px;
    margin-left: 55px;
  }
  a[href="#wrapheader"]:after{
    content: "^";
    padding: 3px 6px 0;
    font-size: 20px;
    border-radius: 15px;
    color: #eee;
    transition: all 0.25s ease;
    text-shadow: none !important;
    box-shadow: inset 1px 1px 3px #000;
  }
  a[href="#wrapheader"]:hover:after{
    color: #fff;
    background: #325375;
    box-shadow: 1px 1px 2px #000;
  }
  b{
    font-size: 12px;
    color: #fff;
  }
  a[title="Click to jump to page…"]{
    color: #68a;
  }
  .helpline{
    background: none;
    color: #0062e6;
  }
  img[alt="Image"]{
    max-width: 100%;
  }
  img[alt="Post new topic"]{
    display: none;
  }
  a[href^="./posting.php?mode=post"]:before{
    color: #bbb;
    text-shadow: 1px 1px 1px #333;
    font-size: 10px;
    font-weight: bold;
    content: "NEW THREAD";
    display: inline-block;
    width: 77px;
    padding: 6px 10px;
    border-color: rgba(0,0,0,.1) rgba(0,0,0,.1) rgba(0,0,0,.25);
    background-color: #1a3a5a;
    background-image: linear-gradient(to bottom,#325375,#1a3a5a);
    background-repeat:no-repeat;
    box-shadow: inset 0 1px 0 rgba(255,255,255,.2),0 1px 2px rgba(0,0,0,.05);
    text-shadow: 1px 1px 1px #111;
    border-radius: 2px;
    transition: all .1s linear;
  }
  a[href^="./posting.php?mode=post"]:hover:before{
    color: #ddd;
    background-position: 0 -14px;
  }  
