    box-shadow: 1px 1px 2px #000;
    border-radius: 4px;
    padding: 8px 10px;
  }
  div.buttons a,.button1,.button2{
    box-shadow: 1px 1px 2px #000;
    border-radius: 3px;
  }
  .post-author{
    box-shadow: 1px 1px 2px #111;
    border-radius: 2px;
  }
  blockquote, .codebox, .attachbox, .inline-attachment{
    background: #2e2e2e;
    border: 0;
    color: #aaa;
    box-shadow: 1px 1px 2px #000;
    border-radius: 3px;
  }
  .rules, .notice{
    background: #262626;
    border-radius: 3px;
    box-shadow: inset 1px 1px 2px #111;
  }
  ul.forums > li.row-new,
  ul.forums > li.pm-new,
  ul.forums > li,
  ul.forums > li.has-pagination,
  .search .post-details{
    background: #1e1e1e;
    border:0;
    border-radius: 3px;
    box-shadow: 1px 1px 2px #000;
    padding: 0.5px 5px;
    margin: 4px 0;
  }
  ul.forums > li.row-pagination{
    padding: 1px 5px 3px;
  }
  
  .block > p.header,
  .forums > p.header{
