    box-shadow: 1px 1px 2px #000;
  }
  #page-body{
    padding: 4px 4px 20px
  }
  span.replies,
  ul.forums a.newest,
  ul.forums span.pages a{
    border-radius: 2px;
    padding: 1px 3px;
    box-shadow: 1px 1px 2px #111;
  }
  ul.forums > li.has-pagination > p:last-child{
    border: 0;
  }
  ul.sub{
    border: 0;
    border-radius: 4px;
    box-shadow: 1px 1px 2px #000;
    padding: 0;
  }
  ul.sub li{
    border-color: #515667;
  }
  ul.sub li:last-of-type{
    background: #911;
    border-radius:  0 0 4px 4px;
  }
  ul.sub li:last-of-type a{
    text-align: center !important;
    text-decoration: none;
    font-size: 0px;
    font-weight: bold;
    padding: 0;
    height: 26px;
    line-height: 26px;
  }
  ul.sub li:last-of-type a:after{
    content: "CLOSE";
    font-size: 18px;
  }
  ul.sub li a:hover{
    background: none;
  }
  img{
    max-width: 100%;
  }
