}

/*[[Modern]]*/
@-moz-document regexp("^[^\.]*\.mobilism\.org/viewtopic\.php.*") {
  .mobil-odd{
    border-radius: 4px;
    background: #1e1e1e;
    box-shadow: inset 1px 1px 4px #161616;
  }
  .mobil-odd p.separator i{
    background: #1e1e1e !important;
  }
}
/**/

/*[[Classic]]*/
@-moz-document regexp("^[^\.]*\.mobilism\.org/viewtopic\.php.*") {
    .tablebg{
    margin: 10px 0;
  }
}
/**/

/*[[Mobile]]*/
@-moz-document regexp("^[^\.]*\.mobilism\.org/portal\.php.*") {
  .spacer{
    background: none !important;
  }
  table td, table th{
    border: 0;
  }
  .tablebg{
    background: #1e1e1e;
    border:0;
    border-radius: 3px;
    box-shadow: 1px 1px 2px #000;
    margin: 4px 0;
  }
  .tablebg > tbody > tr:first-of-type{
    background: linear-gradient(to bottom,#515667,#272a39);
    border-radius: 4px 4px 0 0;
  }
  .tablebg th{
    background: 0;
    margin: 0;
    width: 100%
  }
  .tablebg th[align="left"]{
    display: block;
    font-size: 14px;
    line-height: 22px;
    padding: 0;
    text-align: center;
  }
  .tablebg th[align="center"],
  .tablebg th[align="right"]{
    display: block;
    line-height: 22px;
    padding: 0;
    text-align: left;
  }
  #page-body > table:nth-of-type(2) td[align="left"],
  #page-body > table:nth-of-type(2) td[align="right"]{
    display: block;
    text-align: right;
  }
  .tablebg .spacer[height="1"],
  .tablebg th[align="left"] strong{
    display: none;
  }
}
/**/
