style.css:
@-moz-document domain('pinterest.com'), url-prefix('http://www.pinterest.com/') {
div.Nag, div.Nags { display:none !important; }
div.NagSpacer { display:none !important; }
div.hasFooter { overflow-y: visible !important; }
div.Modal.fullScreen { display: none !important; }
div.pinMetaWrapper { display: none !important; }
div.creditFooter { display: none !important; }
div.creditTitle { display: none !important; }
div.creditImg  { display: none !important; }
div.DiscoveryTile { display: none !important; }
div.ModuleCycler { display: none !important; }
div.EmailInviteMerchandising { display: none !important; }
div.FriendCenterMerchandising { display: none !important; }
}
