style.css:
@-moz-document url-prefix(https://aidoru-online.org) {
/* -- Theme Resets --*/
.nfo {
	font-family: terminal;
	font-size: 10px;
}
hr {
	height: 1px;
	width: auto;
	margin-right: auto;
	margin-left: auto;
	border: 1px dotted #9400be;
}
p {
	margin: 0px;
	padding: 0px;
}
ul, ol {
	list-style-position: inside;
	list-style-type: none;
}
a, img {
	border: 0px none;
	outline: none;
}
h1 {
	font-size: 11pt;
	color: #e776fe;
}
h2 {
	font-size: 10pt;
	color: #444;
}
h3 {
	font-size: 9pt;
	color: #444;
}
h4, h5, h6 {
	font-size: 8pt;
}
table { border-collapse: collapse; }

/* Layout Styles */
body {
	background-color: #111111;
	margin-left: 0px;
	margin-top: 0px;
	margin-right: 0px;
	margin-bottom: 0px;
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: 8pt;
	color: #909090;
}
.wrapper {
	display: block;
	margin: 0px;
	padding: 0px;
	width: 100%;
	position: relative;
}
.header {
	display: block;
	margin: 0px;
	padding: 0px;
	height: px;
	width: 100%;
	position: relative;
}
.logo {
	background: url(data:image/gif;base64,);
	display: block;
	margin: px;
	padding: 0px;
	height: 42px;
	width: 312px;
	position: relative;
	left: 26px;
	top: 26px;
}
	.logo img {
		display: none;
	}
.infobar {
	color: #666;
	display: block;
	margin: 0px;
	padding: 0px;
	clear: right;
	float: right;
	position: absolute;
	z-index: 2;
	top: 20px;
	right: 20px;
}
#main {
	padding: 6px;
	display: block;
	position: relative;
	margin: 0px;
}
.credits {
	text-align: center;
	display: block;
	margin: 0px;
	padding: 10px;
	width: auto;
	line-height: 16px;
}
.avatar {
	max-width: 120px;
	max-height: 120px;
}
.comment-avatar {
	max-width: 80px;
	max-height: 80px;
}

/* -- Form Styles -- */
input, select {
	font-family: Arial, Helvetica, sans-serif;
	margin: 1px;
	padding: 2px;
	border: 1px solid #777;
	background-color: #505050;
}
input {
	font-size: 11px;
}
select {
	font-size: 10px;
	color: rgb(230,0,240);
}
textarea {
	border: 1px solid #aaa;
	padding: 7px;
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	border-radius: 5px;
	-moz-box-shadow: inset 0 2px 4px 1px #CCC;
	-webkit-box-shadow: inset 0 2px 4px 1px#CCC;
	box-shadow: inset 0 2px 4px 1px #CCC;
	background-color: #0000;
}
input[type=reset], input[type=button], input[type=reset], input[type=submit] {
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	border-radius: 5px;
	border: 1px solid #000;
	padding-top: 1px;
	padding-right: 2px;
	padding-bottom: 1px;
	padding-left: 4px;
	margin: 2px;
	-moz-box-shadow: 0 0px 2px 1px #000;
	-webkit-box-shadow: 0 0px 2px 1px#000;
	box-shadow: 0 0px 2px 1px #000;
	font-weight: bold;
	font-family: Arial, Helvetica, sans-serif;
	font-size: 12px;
	color: #fff;
	cursor: pointer; 
	background: #101010;
	}
input[type=reset]:hover, input[type=button]:hover, input[type=reset]:hover, input[type=submit]:hover {
	color: #fff;
	text-shadow: 0px 1px 2px #000;
}
input[type=reset], input[type=button], input[type=reset] {
}
input[type=reset]:hover, input[type=button]:hover, input[type=reset]:hover {
}
input[type=submit] {
}
input[type=submit]:hover {
}
input[type=text] {
	-moz-box-shadow: inset 0 0 0 1px #fff;
	-webkit-box-shadow: inset 0 0 0 1px#fff;
	box-shadow: inset 0 0 0 1px #fff;
	font-size: 13px;
	font-family: Arial, Helvetica, sans-serif;
	border: 1px solid #444;
}
input[type=password] {
	color: #ff0000;
}
input#tsearchtxt {		
		-moz-box-shadow: inset 0 0 0 1px #AAA;		
		-webkit-box-shadow: inset 0 0 0 1px#AAA;		
		box-shadow: inset 0 0 0 1px #AAA;		
		font-size: 13px;		
		font-family: Verdanda;		
		background-color: #1b1b1b;		
		color: #fff;		
		padding: 4px;		
		border: 1px solid rgba(0, 0, 0, 0.2);		
	}		
	.select {		
		display: inline-block;		
		position: relative;		
	}		
	.select select {		
		-moz-box-shadow: inset 0 0 0 1px #AAA;		
		-webkit-box-shadow: inset 0 0 0 1px #AAA;		
		box-shadow: inset 0 0 0 1px #AAA;		
	  outline: none;		
	  -webkit-appearance: none;		
	  -moz-appearance: none;		
	  display: block;		
	  padding: 4px 17px 4px 4px;		
	  margin: 0 0 0 10px;		
			
	  transition: border-color 0.2s;		
	  border: 1px solid rgba(0, 0, 0, 0.2);		
			
	  background: #1b1b1b;		
	  color: #fff;		
	  font-family: Verdanda;
	  font-size: 11px;		
	}		
	.select .arr {		
	  background: #FAFAFA;		
	  position: absolute;		
	  right: 5px;		
	  top: 5px;		
	  bottom: 5px;		
	  width: 13px;		
	  pointer-events: none;		
	}		
	.select .arr:before {		
	  content: '';		
	  position: absolute;		
	  top: 5px;		
	  pointer-events: none;		
	  border-top: 5px solid rgba(0, 0, 0, 0.8);		
	  border-left: 5px solid transparent;		
	  border-right: 5px solid transparent;		
	}

/* -- Link Styles -- */
a:link, a:visited, a:active {
	text-decoration: none;
	color: #bc06ae;
}
a:hover {
	text-decoration: underline;
	color: #d42cbe;
}
a:visited {
		text-decoration: none;
		color: #ff75f0;
	}
.navigation {
	background-image: url(https://aidoru!online.org/themes/default/images/menu.gif);
	background-repeat: repeat-x;
	background-position: top;
	text-align: center;
	display: block;
	margin: 0px;
	padding: 0px;
	height: 27px;
	width: 100%;
}
.menu {
	display: table;
	padding: 0px;
	height: 27px;
	clear: both;
	margin-top: 0px;
	margin-right: auto;
	margin-left: auto;
	width: auto;
}
.menu ul {
	display: table-row;
	margin: 0px;
	padding: 0px;
}
.menu ul li {
	display: table-cell;
	margin: 0px;
	padding: 0px;
}
.menu ul li a {
	font-size: 12px;
	line-height: 27px;
	font-weight: bold;
	color: #b101a2;
	text-decoration: none;
	display: block;
	margin: 0px;
	height: 27px;
	width: auto;
	padding-top: 0px;
	padding-right: 18px;
	padding-bottom: 0px;
	padding-left: 18px;
	text-shadow: 0px 1px 1px #000;
}
.menu ul li a:hover {
	color: #c700ad;
	text-shadow: none;
}
.bMenu {
	display: block;
	margin: 0px;
	padding: 0px;
	width: 100%;
}
.bMenu ul {
	display: block;
	margin: 0px;
	padding: 0px;
	list-style-position: inside;
	list-style-type: circle;
}
.bMenu ul li {
	width: auto;
	color: #666666;
	text-decoration: none;
}
#navigate ul {
	list-style-type: none;
	padding: 0px;
	width: 98%;
	border-bottom-width: 1px;
	border-bottom-style: solid;
	border-bottom-color: #AAA;
	margin-top: 0px;
	margin-right: auto;
	margin-bottom: 0px;
	margin-left: auto;
}
#navigate ul li {
	margin: 0px;
	padding: 0px;
	width: 100%;
	border-collapse: collapse;
	border-top-width: 1px;
	border-right-width: 1px;
	border-left-width: 1px;
	border-top-style: solid;
	border-right-style: solid;
	border-left-style: solid;
	border-top-color: #AAA;
	border-right-color: #AAA;
	border-left-color: #AAA;
}
#navigate ul li a {
	text-align: left;
	text-indent: 5px;
	display: block;
	margin: 0px;
	padding: 0px;
	height: 18px;
	width: 100%;
	font-family: Arial, Helvetica, sans-serif;
	line-height: 18px;
	font-size: 12px;
	color: #909090;
	font-weight: bold;
	background: #101010;
}
