	color: #c700ad;
	text-decoration: none;
	text-indent: 8px;
}
#nMember, #uOnline {
	margin: 2px;
	width: 98%;
	padding: 2px;
	font-family: Verdana, sans-serif;
}
#nMember p, #uOnline p {
	display: inline;
}
#nMember ul, #nMember li, #uOnline ul, #uOnline li {
	display: inline;
	margin: 0;
	padding: 0;
	color: #fff;
}
#nMember, #uOnline {
	margin: 2px;
