/* THESE ARE THE CUSORS, get them at http://www.cursors-4u.com */

html {
    cursor: url('data:image/png;base64,'), progress !important;
}





a:link {
    cursor: url('data:image/png;base64,'), progress !important;
}




::-webkit-scrollbar {
    width: 8px!important;
    height: 10px!important;
}
body::-webkit-scrollbar {
    width: 8px!important;
    height: 10px!important;
}
::-webkit-scrollbar-track {
    background: /*[[str]]*/!important;
    border-radius: 0px!important;
    border: 0px inset black;
}
::-webkit-scrollbar-track:hover {
    background: /*[[strh]]*/!important;
}
::-webkit-scrollbar-track:active {
    background: /*[[stra]]*/!important;
    
}
::-webkit-scrollbar-thumb {
    background-color: /*[[st]]*/ !important;
    min-height: 10px;
    border-radius: 10px!important;
}
::-webkit-scrollbar-thumb:hover {
    background: /*[[sth]]*/!important;
}
::-webkit-scrollbar-thumb:active {
    background: /*[[sta]]*/!important;
