style.css:
 /* 
*  Neopets - Winter Holiday Theme Tweaks
*  Author: mesna               
*  
*  Note: Not the complete theme, just tweaks for those that already have it.
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("neopets.com") {
A:Hover {color: #047960 !important}
ul.dropdown {border: 1px solid #000000 !important;background-color: #4092E2 !important; opacity: .95 !important}
LI:hover UL.dropdown A, LI.over UL.dropdown A {color: #FFFFFF !important}
LI:hover UL.dropdown A:Hover, LI.over UL.dropdown A:hover {color: #B2E1F1 !important}

.sidebarModule {border: 1px solid #000000 !important}
.sidebarTable {border: none !important}
.sidebarHeader {background-color: #B2E1F1 !important; border-bottom: 1px solid #000000 !important; color: #103F6D !important}
.sidebarHeader A:Link, .sidebarHeader A:Hover, .sidebarHeader A:Visited {color: #103F6D !important}
.activePet {border-bottom: 1px solid #000000 !important}

.contentModule {border: 1px solid #000000 !important}
.contentModuleTable, .contentModuleHeader {border: none !important}
.contentModuleContent {border-top: 1px solid #000000 !important}

.marqueeSlotOn A:Hover {color: #B2E1F1 !important}
.newItemSpacer {border-top: 1px solid #000000 !important; border-bottom: 1px solid #E4E4E4 !important}
}
