style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("learnxinyminutes.com") {

    html,
    button,
    input,
    select,
    textarea {
        color: #bbb !important;
        background: #1d1d1d !important;
    }

    td.lang a {
        background: #262626 !important;
        border-bottom: 2px solid #151526 !important;
    }

    pre {
        color: #aaa;
        background: #262626 !important;
    }

    .c {
        color: #554 !important;
    }

    .o,
    .k,
    .kn,
    .ow,
    .kp {
        color: #aaa !important;
    }

    .highlight .n {
        color: #999 !important;
    }

    .nn {
        color: #407B45 !important;
    }

    .nf {
        color: #8F2424 !important;
    }

    .highlight {
        background: #262626;
    }

    th,
    td {
        border-color: #666;
    }

    a {
        color: #4183C4;
    }
    
    .share {
        display: none;
    }

}
