style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('https?://(?!softserve).*8ch.net.*'), regexp('.*oxwugzccvk3dk6tj.onion.*') {
body {
	background: #181818;
	color: #d8d8d8
}
.content_menu {
	background-color: #282828;
}
.button {
	background-color: #282828;
}
header div.subtitle, h1 {
	color: #ab4642;
}
a:link, a:visited, p.intro a.email span.name, a#catalog_search_button {
	color: #7cafc2;
}
a:hover,a:link:hover, a#catalog_search_button:hover {
	color: #86c1b9;
}
a.post_no {
	color: #d8d8d8;
}
a.post_no:hover {
	color: #86c1b9 !important;
}
div.banner {
	background-color: #ab4642;
}
div.post.reply {
	background-color: #282828;
	border-width: 1px;
	border-style: solid;
	border-left-color: #383838;
	border-top-color: #383838;
	border-right-color: #282828;
	border-bottom-color: #282828;
}
div.post.reply.highlighted {
	background-color: #282828;
	border-color: #585858;
}
div.post.reply div.body a {
	color: #7cafc2;
}
div.post.reply div.body a:hover {
	color: #86c1b9;
}
div.post-hover {
	border: 1px solid #181818 !important;
	box-shadow: none !important
}
div.thread:hover {
	background-color: #181818;
	border-color: #000;
}
p.intro span.subject {
	color: #ba8baf;
}
p.intro span.name {
	color: #d8d8d8;
}
.quote {
	color: #a1b56c;
}
span.heading {
	color: #ab4642;
}
form table tr th {
	background: #282828;
	border: 1px solid #111;
	color: #d8d8d8;
}
div.ban h2 {
	background: #383838;
	color: #d8d8d8;
}
div.ban {
	border-color: #ba8baf;
	background: #282828;
}
div.pages {
	background: #181818;
	border-color: #181818;
}
div.pages a.selected {
	color: #86c1b9;
	font-weight: 700;
}
.desktop-style div.boardlist:not(.bottom) {
	background-color: #282828 !important;
	color: #d8d8d8;
}
div.boardlist:nth-of-type(1) {
	border-bottom: 1px solid #111 !important;
	box-shadow: 0 0 3px 0 #111;
}
div.boardlist a {
	color: #7cafc2;
}
hr {
	background-color: #282828;
	border: 0;
	height: 1px;
}
div#options_div {
	background-color: #282828;
}
div.options_tab_icon {
	color: #aaa;
}
div.options_tab_icon:hover {
	background-color: #111;
}
div.options_tab_icon.active {
	color: #ab4642;
}
div.blotter {
	color: #ab4642;
}
span.omitted {
	color: #707070;
}
p.intro a,span.omitted a {
	text-decoration: none;
}
form#quick-reply {
	padding-right: 1px;
	border: 1px solid #111;
}
span.capcode {
	background-color: #000;
	padding: 2px 5px;
	border-radius: 10px;
}
div#watchlist {
	border: 1px solid #111;
	background-color: #282828;
}

.theme-catalog div.thread:hover {
	background: #3b3b3b;
	border-color: #5b5b5b;
}
pre.prettyprint{
	padding: 4px;
	border: 1px solid #585858;
}

.intro span.subject { color: #7cafc2; }
.intro span.name { color: #a1b56c; }

#alert_div { background-color: #282828; }

/* Code */
.str { color: #a1b56c; } /* string */
.kwd { color: #ba8baf; font-weight: bold; }
.com { color: #585858; } /* comment */
.typ { color: #86c1b9; } /* type */
.lit { color: #f7ca88; } /* literal */
.pun,.opn,.clo { color: #e8e8e8; }    /* punctuation */
.pln { color: #e8e8e8; }    /* plaintext */
.tag { color: #7cafc2; font-weight: bold; } /* html/xml tag */
.atn { color: #bdb76b; font-weight: bold; } /* attribute name */
.atv { color: #ffa0a0; } /* attribute value */
.dec { color: #98fb98; } /* decimal */

/* Home Page */
.box {
	background: #282828 !important;
	border-color: #383838;
	color: inherit;
}
.box-title {
	background: #585858;
	color: #181818;
}

/* Board Table */
table tbody tr:nth-child(even),
table.board-list-table tr:nth-of-type( even ) .board-tags .board-cell {
	background: #282828;
}
table.board-list-table .board-tags .board-cell:hover {
	background: #181818; 
}
table thead th {
	background: #585858;
	color: #181818;
}
tbody.board-list-omitted td {
	background: #585858;
}

/* Velocious Threads */
.box-content > .col:nth-child(odd) {
	background-color: #383838 !important;
}

/* Inputs */
input[type="text"], input[type="password"], textarea {
	background: #282828;
	border: 1px solid #383838;
	color: #d8d8d8;
}
}
